    addRayHit(hit, sphereIsect(ray, Translate(vec3(1.0, 0.4, -3.0)) * Scale(vec3(0.4)), 7));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(2.0, 0.4, -3.0)) * Scale(vec3(0.4)), 8));

    // Small sphere lights
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-2.0, 0.2, -2.25)) * Scale(vec3(0.2)), 2));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-1.0, 0.2, -2.25)) * Scale(vec3(0.2)), 2));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(0.0, 0.2, -2.25)) * Scale(vec3(0.2)), 2));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(1.0, 0.2, -2.25)) * Scale(vec3(0.2)), 2));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(2.1, 0.2, -2.25)) * Scale(vec3(0.2)), 2));

    // Large opaque glass spheres
    addRayHit(hit, sphereIsect(ray, Translate(vec3(3.0, 0.75, -4.0)) * Scale(vec3(0.75)), 9));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-3.0, 0.75, -4.0)) * Scale(vec3(0.75)), 9));

    // Sphere lights over the opaque glass spheres
    addRayHit(hit, sphereIsect(ray, Translate(vec3(3.0, 1.9, -4.0)) * Scale(vec3(0.2)), 2));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-3.0, 1.9, -4.0)) * Scale(vec3(0.2)), 2));

    return hit;
}

Material getMaterial(in vec2 surfUv, in int objId) {
    Material mtl = Material(vec3(0.0), vec3(0.0), 0.0, 0.0, vec3(0.0));

    // Ground plane
    if (objId == 1) mtl = Material(vec3(1.0, 0.4, 0.2), vec3(0.0), 0.5, 0.7, vec3(1.0, 0.4, 0.2));

    // Big sphere light
    if (objId == 2) mtl = Material(vec3(0.0), vec3(2.0), 0.0, 0.0, vec3(0.0));

    // Silver metallic spheres
    if (objId == 3) mtl = Material(vec3(0.0), vec3(0.0), 1.0, 0.0, vec3(1.0));

    // Green metallic spheres with increasing roughness
    if (objId == 4) mtl = Material(vec3(0.0, 1.0, 0.0), vec3(0.0), 1.0, 0.0, vec3(0.0, 1.0, 0.0));
    if (objId == 5) mtl = Material(vec3(0.0, 1.0, 0.0), vec3(0.0), 1.0, 0.25, vec3(0.0, 1.0, 0.0));
    if (objId == 6) mtl = Material(vec3(0.0, 1.0, 0.0), vec3(0.0), 1.0, 0.5, vec3(0.0, 1.0, 0.0));
    if (objId == 7) mtl = Material(vec3(0.0, 1.0, 0.0), vec3(0.0), 1.0, 0.75, vec3(0.0, 1.0, 0.0));
    if (objId == 8) mtl = Material(vec3(0.0, 1.0, 0.0), vec3(0.0), 1.0, 1.0, vec3(0.0, 1.0, 0.0));

    // Large opaque glass spheres
    if (objId == 9) mtl = Material(vec3(1.0), vec3(0.0), 0.5, 0.0, vec3(1.0));

    return mtl;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    uint rngState = uint(uint(fragCoord.x) * uint(1973) + uint(fragCoord.y) * uint(9277) + uint(iFrame) * uint(26699)) | uint(1);

    // Load last frame
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);

    // Jitter UVs for antialiasing
    fragCoord += vec2(rand01(rngState), rand01(rngState));
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Camera ray
    mat3 cam = getCameraBasis(vec2(0.0, -0.8));
    Ray ray = Ray(vec3(0.0, 4.0, -0.5), normalize(cam * vec3(uv, 1.0)));

    // Pathtrace!
    vec3 acc = vec3(0.0);
    vec3 throughput = vec3(1.0);
    for (int bounce=0; bounce < MAX_BOUNCES; bounce++) {
        HitInfo hit = traceRay(ray);
        if (hit.hitDist < 0.0) break;
        Material mtl = getMaterial(hit.surfUv, hit.objId);

        hit.surfNor = normalize(hit.surfNor); // Normalize the surface normal as a final step
        hit.surfNor *= sign(dot(hit.surfNor, -ray.dir)); // Flip towards the camera

        // Accumulate bounce light
        acc += mtl.emissive * throughput;

        // Step to the surface
        ray.pos = hit.hitPos + hit.surfNor * EPSILON;

        // Choose specular reflection with chance percentSpecular
        if (rand01(rngState) < mtl.percentSpecular) {
            vec3 diffuseRay = normalize(hit.surfNor + randUnitVec(rngState));
            vec3 specularRay = reflect(ray.dir, hit.surfNor);
            ray.dir = normalize(mix(specularRay, diffuseRay, mtl.roughness * mtl.roughness));
            throughput *= mtl.specularColor;
        }

        // Reflect diffusely otherwise
        else {
            ray.dir = normalize(hit.surfNor + randUnitVec(rngState));
            throughput *= mtl.albedo;
        }

        // Russian roulette optimization
        float stopChance = max(throughput.r, max(throughput.g, throughput.b));
        if (rand01(rngState) > stopChance) break;
        throughput /= stopChance;
    }

    // Accumulate frame
    fragColor.rgb += acc;
    fragColor.a += 1.0;

    // Restart on mouse down
    if (iMouse.z > 0.0) {
        fragColor = vec4(0.0);    
    }
}

