float zoom = 0.6;
float timeMul = 1.0;
float colorDistortion = 0.05;
float rays = 0.3;
float lMul = 9.0;
float tMul = 2.0;
float a = 1.;
float brightness = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.y;
    uv = (2.*fragCoord - (fragColor.xy = iResolution.xy) ) / fragColor.y ;

	uv *= zoom;

	vec3 color = vec3(0.0);

	float l = length(uv);
	float t = iTime * timeMul;
	vec2 uv1;

	for (int i = 0; i < 3; i++) {
		t += colorDistortion;
		uv1 = uv + uv / l * (sin(t) + rays) * abs(sin(l*lMul - t*tMul));

		float c = length(abs(mod(uv1, a) - (a/2.)));
