}

// Some useful functions
vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x * 34.0) + 1.0) * x); }

// Description : GLSL 2D simplex noise function
//      Author : Ian McEwan, Ashima Arts
//  Maintainer : ijm
//     Lastmod : 20110822 (ijm)
//     License :
//  Copyright (C) 2011 Ashima Arts. All rights reserved.
//  Distributed under the MIT License. See LICENSE file.
//  https://github.com/ashima/webgl-noise
float snoise(vec2 v) {
	// Precompute values for skewed triangular grid
	vec4 C = vec4(
		0.211324865405187, //(3.0-sqrt(3.0))/6.0
		0.366025403784439, // 0.5*(sqrt(3.0)-1.0)
		-0.577350269189626, // -1.0 + 2.0 * C.x
		0.024390243902439); // 1.0 / 41.0

	//C.w += time * 0.0005;

	// First corner (x0)
	vec2 i  = floor(v + dot(v, C.yy));
	vec2 x0 = v - i + dot(i, C.xx);

	// Other two corners (x1, x2)
	vec2 i1 = vec2(0.0);
	i1 = (x0.x > x0.y) ? vec2(1.0, 0.0):vec2(0.0, 1.0);
	vec2 x1 = x0.xy + C.xx - i1;
	vec2 x2 = x0.xy + C.zz;

	// Do some permutations to avoid
	// truncation effects in permutation
	i = mod289(i);
	vec3 p = permute(permute(i.y + vec3(0.0, i1.y, 1.0)) + i.x + vec3(0.0, i1.x, 1.0 ));

	vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x1,x1), dot(x2,x2)), 0.0);
	m = m*m;
	m = m*m;

	// Gradients:
	//  41 pts uniformly over a line, mapped onto a diamond
	//  The ring size 17*17 = 289 is close to a multiple of 41 (41*7 = 287)

	vec3 x = 2.0 * fract(p * C.www) - 1.0;
	vec3 h = abs(x) - 0.5;
	vec3 ox = floor(x + 0.5);
	vec3 a0 = x - ox;

	// Normalise gradients implicitly by scaling m
	// Approximation of: m *= inversesqrt(a0*a0 + h*h);
	float a = 1.79284291400159;
	float b = 0.85373472095314;
	m *= a - b * (a0*a0 + h*h);

	// Compute final noise value at P
	vec3 g = vec3(0.0);
	g.x  = a0.x  * x0.x  + h.x  * x0.y;
	g.yz = a0.yz * vec2(x1.x,x2.x) + h.yz * vec2(x1.y,x2.y);
	return 130.0 * dot(m, g);
}

float fbm(vec2 uv, int octaves, float amplitude, float frequency, vec2 shift, float lacunarity, float gain) {
	float value = 0.0;
    for (int i = 0; i < octaves; ++i) {
        float sn = snoise(uv * frequency);
        value += amplitude * sn;
        uv += shift;
        frequency *= lacunarity;
		amplitude *= gain;
	}
	return value;
}

float easySmoothstep(float edge, float smoothness, float x){
	return smoothstep(edge - smoothness, edge + smoothness, x);
}

float line(float uv, float pos, float size) {
	return 1.0 - easySmoothstep(size * 0.5, 0.05, abs(uv - pos));
}

float changeRange(float iValue, float iMin, float iMax, float fMin, float fMax) {
	float fValue = (iValue-iMin) / (iMax-iMin);
	fValue *= fMax - fMin;
	fValue += fMin;
	return fValue;
}

vec2 scale(in vec2 original, in vec2 scale, in vec2 pivot){
	scale = 1.0 / scale;
	mat2 s = mat2(scale.x, 0.0, 0.0, scale.y);
	vec2 final = original;
	final -= pivot;
	final *= s;
	final += pivot;
	return final;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    vec3 col = vec3(0.);

    //uv.x += iTime * 0.1;

    float n = fbm(uv * 0.3 + vec2(iTime * 0.1, 0.), 1, 1.0, 4.0, vec2(0.), 2.0, 0.5);

	vec2 uvR = scale(uv, vec2(1.0) + n*0.05, vec2(0.5));

    //grid
    vec2 uvGrid = fract(uvR * 10.);

    float lx = 1.0-line(uvGrid.x, 0.025, 0.05);
	float ly = 1.0-line(uvGrid.y, 0.025, 0.05);
	col = vec3(1.0) * min(lx, ly);

	float shade = changeRange(n, -1.0, 1.0, 0.0, 1.0);
	col *= vec3(1.0) * shade;

    fragColor = vec4(col, 1.);
}

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float map(vec3 p){
    p.xy*=rot(iTime*.5);
    p.xz*=rot(iTime*.3);
    float h=sin(iTime)*.5+.5;

    // repeat x=0 slice between -h to h which means this line stretches simple circle align x-axis 
    p.x-=clamp(p.x,-h,h);

    // This block itself makes a dounut by the following steps:
    // 1. calculate distance from edge of circle(radius is 0.3)
    // 2. SDF of circle in a space of Z and distance from the circle which looks a donut in 3D space.
    return length(vec2(
        length(p.xy)-.3, // 1
        p.z))-.1; // 2
}

