vec3 fin(vec2 x) {
    return vec3(x, 1);
}

vec3 inf(vec2 x) {
    return vec3(x, 0);
}

vec2 hom(vec3 x) {
    return x.xy / x.z;
}

void mainImage(out vec4 C, in vec2 O0)
{
    f = 5. / iResolution.y;
    vec2 O = world(O0);

    vec2 cam = vec2(3, 3) + .1 * f;
    vec2 a0 = cam + vec2(-10, 1) / 10.;
    vec2 b0 = cam + vec2(1, -10) / 10.;
    if (iMouse.z > 0.)
        cam = world(iMouse.xy + .1);

    /*
    Invariants A.1 and A.2 imply that without further constraints,
    you can't infer camera parameters from a single image.

    Yet, the map between points on the line and projected points
    is exactly 1:1. This can be seen as follows:
    - Given distinct points A, B, T and "the line".
        - and T not in "the line".
        - and T not in line AB.
    - For all points X in "the line" (including point at infinity):
        - The line XT joins X and the projection target T.
        - This line XT meets the screen line AB at exactly one point (possibly at infinity).
    - For all points X in the line AB (including point at infity):
        - The line XT joins X and the projection target T.
        - This line XT meets "the line" at exactly one point (possibly at infinity).
    -> The image can be inferred from the world and vice versa.

    Things to try:
        - "cf = 1": Intersection between AB and "the line" stays fixed.
        - "sf, cf = 0, 1": Above + Space stretches/squishes along y-axis.
    */

    // Invariant A.1: Y-scale perpendicular to line does not move projected points
    float hf = .8 + .2 * cos(iTime / 3.);
    cam.y *= hf;
    a0.y*=hf;
    b0.y *= hf;

    // Invariant A.2: Y-Shear perpendicular to line does not move projected points
    float sf = .5 * cos(iTime / 2.);
    //sf = 0.;
    cam.x += cam.y * sf;
    a0.x += a0.y * sf;
    b0.x += b0.y * sf;

    // Invariant B: Uniform scale around projection target does not move projected points
    // This just moves the line AB without changing its direction.
    float cf = 1. + cos(iTime);
    //cf = 1.;
    vec2 a = a0 + (a0 - cam) * cf;
    vec2 b = b0 + (b0 - cam) * cf;

    C = vec4(0);
    blend(C, vec3(0,0.2,1), abs(O.y) / f);
    blend(C, vec3(.2,.2,.2), length(O-cam) / f - 2.);
    blend(C, vec3(.2,.2,.2), line(O, cam, a, 0., 1e2) / f);
    blend(C, vec3(.2,.2,.2), line(O, cam, b, 0., 1e2) / f);
    blend(C, vec3(.2,.2,.2), length(O-a0) / f - 2.);
    blend(C, vec3(.2,.2,.2), length(O-b0) / f - 2.);
    vec3 ab = cross(fin(a), fin(b));
    {
        for (float i=-20. ; i<=20. ; i++)
        {
            vec2 s = vec2(1, 0) * i;
            vec2 t = hom(cross(cross(fin(s), fin(cam)), ab));
            blend(C, vec3(0,0.2,1), line(O, t, s) / f);
        }
        vec2 v = vec2(-1, 0);
        vec2 t = hom(cross(cross(inf(v), fin(cam)), ab));
        blend(C, vec3(0,0.2,1), arrow(O, t, t+v) / f);

        blend(C, vec3(1,0.2,0), line(O, a, b) / f);
        blend(C, vec3(.5,0,0), line(O, a, b, -1e2, 0.) / f);
        blend(C, vec3(.5,0,0), line(O, a, b, 1., 1e2) / f);
    }

    f = 1. / iResolution.x;
    O = wcam(O0);
    {
        blend(C, vec3(0), -(O.y / f + iResolution.x * .06));
        vec2 ca = floor(vec2(0.1, -0.01) / f) * f;
        vec2 cb = floor(vec2(0.9, -0.01) / f) * f;
        blend(C, vec3(1,0.2,0), line(O, ca, cb) / f);
        blend(C, vec3(.5,0,0), line(O, ca, cb, -1e2, 0.) / f);
        blend(C, vec3(.5,0,0), line(O, ca, cb, 1., 1e2) / f);
        blend(C, vec3(.2,.2,.2), line(O, ca, ca-vec2(0,.02)) / f);
        blend(C, vec3(.2,.2,.2), line(O, cb, cb-vec2(0,.02)) / f);
        for (float i=-20. ; i<=20. ; i++)
        {
            vec2 s = vec2(1, 0) * i;
            vec2 t = hom(cross(cross(fin(s), fin(cam)), ab));
            t = ca + (cb - ca) * dot(t - a, b - a) / dot(b - a, b - a);
            blend(C, vec3(0,0.2,1), line(O, t, t-vec2(0,.02)) / f);
        }
        vec2 v = vec2(-1, 0);
        vec2 t = hom(cross(cross(inf(v), fin(cam)), ab));
        t = ca + (cb - ca) * dot(t - a, b - a) / dot(b - a, b - a);
        blend(C, vec3(0,0.2,1), arrow(O, t, t+vec2(0,-.04)) / f);
    }

    C = sqrt(C);
}

vec2 world(vec2 O)
{
    O -= floor(iResolution.xy / 2.) + .5;
    O *= f;
    O += vec2(0, 2);
    return O;
}

vec2 wcam(vec2 O)
{
    O -= floor(vec2(0, 1) * iResolution.xy) + .5;
    O *= f;
    O += vec2(0);
    return O;
}

