vec2 world(vec2 O);
vec2 wcam(vec2 O);

float line(vec2 p, vec2 a, vec2 b, float c0, float c1)
{
    p -= a;
    b -= a;
    float u = clamp(dot(p, b) / dot(b,b), c0, c1);
    return length(p - b*u);
}

float line(vec2 p, vec2 a, vec2 b)
{
    return line(p, a, b, 0., 1.);
}

float arrow(vec2 p, vec2 a, vec2 b)
{
    a -= b;
    p -= b;
    vec2 u = .2 * a;
    vec2 v = .4 * vec2(-u.y, u.x);
    return min(
        line(p, a, vec2(0), 0., 1.),
        min(
            line(p, vec2(0), u+v, 0., 1.),
            line(p, vec2(0), u-v, 0., 1.)
        )
    );
}

float f;

void blend(inout vec4 C, vec3 v, float r)
{
    float a = smoothstep(1.0, 0.0, r);
    C = mix(C, vec4(v, 1), a);
}

