// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
float sRGB(float t) { return mix(1.055*pow(t, 1./2.4) - 0.055, 12.92*t, step(t, 0.0031308)); }
// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(in vec3 c) { return vec3 (sRGB(c.x), sRGB(c.y), sRGB(c.z)); }

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

// License: Unknown, author: Unknown, found: don't remember
float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

mat3 rot_z(float a) {
  float c = cos(a);
  float s = sin(a);
  return mat3(
      c,s,0
    ,-s,c,0
    , 0,0,1
    );
}

mat3 rot_y(float a) {
  float c = cos(a);
  float s = sin(a);
  return mat3(
      c,0,s
    , 0,1,0
    ,-s,0,c
    );
}

mat3 rot_x(float a) {
  float c = cos(a);
  float s = sin(a);
  return mat3(
      1, 0,0
    , 0, c,s
    , 0,-s,c
    );
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/distfunctions2d
float box(vec2 p, vec2 b) {
  vec2 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/smin
float pmin(float a, float b, float k) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// http://mercury.sexy/hg_sdf/
vec2 mod2(inout vec2 p, vec2 size) {
  vec2 c = floor((p + size*0.5)/size);
  p = mod(p + size*0.5,size) - size*0.5;
  return c;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/intersectors
vec2 rayBox(vec3 ro, vec3 rd, vec3 boxSize, out vec3 outNormal)  {
  vec3 m = 1.0/rd; // can precompute if traversing a set of aligned boxes
  vec3 n = m*ro;   // can precompute if traversing a set of aligned boxes
  vec3 k = abs(m)*boxSize;
  vec3 t1 = -n - k;
  vec3 t2 = -n + k;
  float tN = max( max( t1.x, t1.y ), t1.z );
  float tF = min( min( t2.x, t2.y ), t2.z );
  if( tN>tF || tF<0.0) return vec2(-1.0); // no intersection
  outNormal = -sign(rd)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);
  return vec2( tN, tF );
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/intersectors
float rayPlane(vec3 ro, vec3 rd, vec4 p) {
  return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/intersectors
float raySphere4(vec3 ro, vec3 rd, float ra) {
  float r2 = ra*ra;
  vec3 d2 = rd*rd; vec3 d3 = d2*rd;
  vec3 o2 = ro*ro; vec3 o3 = o2*ro;
  float ka = 1.0/dot(d2,d2);
  float k3 = ka* dot(ro,d3);
  float k2 = ka* dot(o2,d2);
  float k1 = ka* dot(o3,rd);
  float k0 = ka*(dot(o2,o2) - r2*r2);
  float c2 = k2 - k3*k3;
  float c1 = k1 + 2.0*k3*k3*k3 - 3.0*k3*k2;
  float c0 = k0 - 3.0*k3*k3*k3*k3 + 6.0*k3*k3*k2 - 4.0*k3*k1;
  float p = c2*c2 + c0/3.0;
  float q = c2*c2*c2 - c2*c0 + c1*c1;
  float h = q*q - p*p*p;
  if( h<0.0 ) return -1.0; //no intersection
  float sh = sqrt(h);
  float s = sign(q+sh)*pow(abs(q+sh),1.0/3.0); // cuberoot
  float t = sign(q-sh)*pow(abs(q-sh),1.0/3.0); // cuberoot
  vec2  w = vec2( s+t,s-t );
  vec2  v = vec2( w.x+c2*4.0, w.y*sqrt(3.0) )*0.5;
  float r = length(v);
  return -abs(v.y)/sqrt(r+v.x) - c1/r - k3;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/intersectors
vec3 normalSphere4(in vec3 pos) {
  return normalize(pos*pos*pos);
}

vec3 skyColor1(vec3 ro, vec3 rd) {
  const vec3 lightCol1 = vec3(8.0/8.0,7.0/8.0,6.0/8.0);
  const vec3 lightCol2 = vec3(8.0/8.0,5.0/8.0,5.0/8.0);
  const vec3 lightDir1 = normalize(vec3(-0.3, 0.15, 1.0));
  const vec3 lightDir2 = normalize(vec3(-0.33,  -0.2, -1.0));

  float ld1      = max(dot(lightDir1, rd), 0.0);
  float ld2      = max(dot(lightDir2, rd), 0.0);
  float yf       = (0.5+0.5*rd.y);
  yf *= yf;
  yf *= yf;
  vec3 final     = vec3(vec3(0.25, 0.2, 0.3))*yf;

  if ((rd.y > abs(rd.x)*1.0) && (rd.y > abs(rd.z*0.25))) final = vec3(2.0)*rd.y;
  float roundBox = length(max(abs(rd.xz/max(0.0,rd.y))-vec2(0.9, 4.0),0.0))-0.1;
  final += vec3(0.8)* pow(clamp(1.0 - roundBox*0.5, 0.0, 1.0), 6.0);

  final += pow(lightCol1, vec3(2.0, 1.5, 1.5)) * pow(ld1, 8.0);
  final += 8.0*lightCol1 * pow(ld1, 400.0);
  final += pow(lightCol2, vec3(2.0, 1.5, 1.5)) * pow(ld2, 8.0);
  final += 8.0*lightCol2 * pow(ld2, 400.0);
  return final;
}

vec3 skyColor2(vec3 ro, vec3 rd) {
  const vec3 lightDir1 = normalize(vec3(-0.3, 0.25, 1.0));
  const vec3 lightDir2 = normalize(vec3(-0.33,  -0.3, -1.0));
  const vec3 hiLightCol1 = HSV2RGB(vec3(0.6, 0.5, 1.0))*8.0;
  const vec3 loLightCol1 = HSV2RGB(vec3(0.9, 0.5, 1.0))*2.0;
  const vec3 hiLightCol2 = HSV2RGB(vec3(0.1, 0.5, 1.0))*8.0;
  const vec3 loLightCol2 = HSV2RGB(vec3(0.5, 0.5, 1.0))*2.0;
  const vec3 skyCol      = HSV2RGB(vec3(0.7, 0.75, 0.25));

  float ld1      = max(dot(lightDir1, rd), 0.0);
  float ld2      = max(dot(lightDir2, rd), 0.0);

  vec3 nb;
  const vec3 boxDim = vec3(1000.0, 500.0, 1000.0);
  vec2 db = rayBox(ro, rd, boxDim, nb);

  vec3 p = ro+rd*db.y;

  vec3 mp = p;

  vec3 dp = abs(mp);
  vec3 sel = abs(nb);
  vec3 d3 = mix(dp, boxDim*2.0, sel);
  float d = d3.x;
  const float sm = 20.0;
  d = pmin(d, d3.y, sm);
  d = pmin(d, d3.z, sm);

  vec3 m3 = sin(TIME+3.0*p/boxDim);
  float m = mix(0.75, 0.9, tanh_approx(m3.x+m3.y+m3.z));
  m *= m;
  m *= m;
  vec3 hsv = vec3(0.4+mix(0.45,0.0, m), tanh_approx(0.005*mix(50.0, 10.0, m)*d), 1.0);
  vec3 gcol = 1.5*hsv2rgb(hsv)*exp(-0.005*mix(30.0, 10.0, m)*d);


  float dif = max(dot(rd, -nb), 0.0);
  dif *= dif;
  dif *= dif;

  float yf       = (0.5+0.5*rd.y);
  yf *= yf;

  vec3 col = skyCol*yf;

  col += gcol;
  if (rd.y > 0.0) {
    const vec3 gcol = HSV2RGB(vec3(0.8, 0.33, 1.0));
    vec3 pos  = ro + db.y*rd;
    vec2 pp = pos.xz;
    float db = box(pp, boxDim.xz*0.7)-50.0;

    col += vec3(2.0)*gcol*rd.y*smoothstep(10.25, 0.0, db);
    col += vec3(0.8)*gcol*exp(-0.0125*max(db, 0.0));
  }

  col += loLightCol1 * pow(ld1, 10.0);
  col += hiLightCol1 * pow(ld1, 800.0);
  col += loLightCol2 * pow(ld2, 10.0);
  col += hiLightCol2 * pow(ld2, 800.0);

  return col;
}

float blobs(vec2 p) {
  // Generates a grid of dots
  vec2 bp = p;
  vec2 bn = mod2(bp, vec2(3.0));

  vec2 dp = p;
  vec2 dn = mod2(dp, vec2(0.25));
  float ddots = length(dp);

  // Blobs
  float dblobs = 1E6;
  for (int i = 0; i < 5; ++i) {
    float dd = circle(bp-1.0*vec2(sin(TIME+float(i)), sin(float(i*i)+TIME*sqrt(0.5))), 0.1);
    dblobs = pmin(dblobs, dd, 0.35);
  }

  float d = 1E6;
  d = min(d, ddots);
  // Smooth min between blobs and dots makes it look somewhat amoeba like
  d = pmin(d, dblobs, 0.35);
  return d;
}

vec3 skyColor3(vec3 ro, vec3 rd) {
  const vec3 gcol = HSV2RGB(vec3(0.45, 0.6, 1.0));
  vec3 col = clamp(vec3(0.00125/abs(rd.y))*gcol, 0.0, 1.0);


  float tp0  = rayPlane(ro, rd, vec4(vec3(0.0, 1.0, 0.0), 4.0));
  float tp1  = rayPlane(ro, rd, vec4(vec3(0.0, -1.0, 0.0), 6.0));
  float tp = tp1;
  tp = max(tp0,tp1);
  if (tp > 0.0) {
    vec3 pos  = ro + tp*rd;
    const float fz = 0.25;
    const float bz = 1.0/fz;
    vec2 bpos = pos.xz/bz;
    float db = blobs(bpos)*bz;
    db = abs(db);
    vec2 pp = pos.xz*fz;
    float m = 0.5+0.25*(sin(3.0*pp.x+TIME*2.1)+sin(3.3*pp.y+TIME*2.0));
    m *= m;
    m *= m;
    pp = fract(pp+0.5)-0.5;
    float dp = pmin(abs(pp.x), abs(pp.y), 0.125);
    dp = min(dp, db);
    vec3 hsv = vec3(0.4+mix(0.15,0.0, m), tanh_approx(mix(50.0, 10.0, m)*dp), 1.0);
    vec3 pcol = 1.5*hsv2rgb(hsv)*exp(-mix(30.0, 10.0, m)*dp);

    float f = 1.0-tanh_approx(0.1*length(pos.xz));
    col = mix(col, pcol , f);
  }


  if (tp1 > 0.0) {
    vec3 pos  = ro + tp1*rd;
    vec2 pp = pos.xz;
    float db = box(pp, vec2(6.0, 9.0))-1.0;

    col += vec3(2.0)*gcol*rd.y*smoothstep(0.25, 0.0, db);
    col += vec3(0.8)*gcol*exp(-0.5*max(db, 0.0));
  }


  return col;
}

vec3 skyColor(vec3 ro, vec3 rd) {
  float tp = mod(TIME, PERIOD);
  float np = mod(floor(TIME/PERIOD), 3.0);
  vec3 col;
  if (np == 0.0) {
    col = skyColor1(ro, rd);
  } else if (np == 1.0) {
    col = skyColor2(ro, rd);
  } else {
    col = skyColor3(ro, rd);
  }

  return col*smoothstep(0.25, 1.0, tp)*smoothstep(PERIOD-0.25, PERIOD-1.0, tp);
}


vec3 color(vec3 ro, vec3 rd) {
  vec3 skyCol = SKYCOLOR(ro, rd);

  mat3 rot = 
    rot_x(0.25*TIME)
    *rot_y(0.33*TIME)
    *rot_z(0.45*TIME)
    ;
  mat3 irot = transpose(rot);

  vec3 bro = rot*ro;
  vec3 brd = rot*rd;

  float bi = raySphere4(bro, brd, 3.0);

  if (bi > -1.0) {

    vec3 bp = bro + bi*brd;
    vec3 bn = normalSphere4(bp);
    vec3 p = irot*bp;
    vec3 n = irot*bn;
    vec3 r = reflect(rd, n);
    float bf = 1.0+dot(rd,n);
    float fre = bf;
    fre *= fre;
    vec3 rsky = SKYCOLOR(p, r);
    vec3 col = mix(0.125, 1.0, fre)*rsky;
    float rdif = dot(-rd, n);
    rdif *= rdif;
    col += 0.25*mix(0.0125, 0.05, rdif)*vec3(1.0);
    return mix(skyCol, col, smoothstep(1.0, 0.85, bf));
  }

  return skyCol;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy; 
  vec2 p = -1.0 + 2.0*q;
