///https://www.shadertoy.com/view/7tKSWt
float sdStairs( in vec2 p, in vec2 wh, in float n )
{
    // base
    vec2 ba = wh*n;
    float d = min(dot2(p-vec2(clamp(p.x,0.0,ba.x),0.0)), 
                  dot2(p-vec2(ba.x,clamp(p.y,0.0,ba.y))) );
    float s = sign(max(-p.y,p.x-ba.x) );

    // steps repetition
#if 1
    float dia = length(wh);
    p = mat2(wh.x,-wh.y, wh.y,wh.x)*p/dia;
    float lid = clamp(round(p.x/dia),0.0,n-1.0);
    p.x = p.x - lid*dia;
    p = mat2(wh.x, wh.y,-wh.y,wh.x)*p/dia;
#else
    float dia2 = dot(wh,wh);
    p = mat2(wh.x,-wh.y,wh.y,wh.x)*p;
    float lid = clamp(round(p.x/dia2),0.0,n-1.0);
    p.x = p.x - lid*dia2;
    p = mat2(wh.x,wh.y,-wh.y,wh.x)*p/dia2;
#endif    

    // single step
    float hh = wh.y/2.0;
    p.y -= hh;

    if( p.y>hh*sign(p.x) ) s=1.0;
    p = (lid<0.5 || p.x>0.0) ? p : -p;

    d = min( d, dot2(p-vec2(0.0,clamp(p.y,-hh,hh))) );
    d = min( d, dot2(p-vec2(clamp(p.x,0.0,wh.x),hh)) );

    return sqrt(d)*s;
}


float opScaleEscalera(vec2 p, vec2 med, float num )
{
    float s=0.25;
    float d1= sdStairs( p/s, med, num )*s;
    return d1;
}

///modificado y reparacion por..... pyBlob,
float opBlock(float d2, float z, float r)
{
    float d1 = abs(z) - r;
    return length(max(vec2(d1, d2), 0.)) + min(max(d1, d2), 0.);
}

///modificado y reparacion por..... pyBlob,
vec3 GetDist(vec3 p  ) 
{	vec3 res= vec3(9999.0, -1.0,-1.0);  vec3 p0=p;

    float planeDist1 = p.y+5.0;  //piso inf
    float planeDist2 = 45.0-p.y;  //piso sup
    float planeDist3 = p.x+30.0; //pared izq
    float planeDist4 = 30.0-p.x;  //pared der
    float planeDist5 = -p.z+60.0;  //pared frente
    float planeDist6 = p.z+60.0;  //pared atras

    res = opU3(res, vec3(planeDist1,100.0,-7.0));
    res = opU3(res, vec3(planeDist2,100.0,-7.0));
    res = opU3(res, vec3(planeDist3,100.0,-7.0));
    res = opU3(res, vec3(planeDist4,100.0,-7.0));

    p.y = p.y-5.0;

    vec3 p1 = p; // sup
    p1.z = opRep1D(p1.z, 30.0);

    vec3 p2 = p; // inf
    p2.z -= 15.;
    // opRep only works correctly, when the repeated object is symmetric around the middle of the repetition.
    // If you want to offset the bottom stairs, the repetition should be separate for top/bottom stairs.
    p2.z = opRep1D(p2.z, 30.0);

    float da2 = opScaleEscalera(p1.xy-vec2(7.0,-10.0), vec2(10.0,10.0), 10.0 );
    float dinter1 = opBlock(da2, p1.z, 5.);
    res = opU3(res, vec3(dinter1,101.0,-1.0));

    float db2 = opScaleEscalera(vec2(-p1.x,p1.y)-vec2(7.0,-10.0), vec2(10.0,10.0), 10.0 );
    float dinter2 = opBlock(db2, p1.z, 5.);
    res = opU3(res, vec3(dinter2,101.0,-1.0));

    float dc2 = opScaleEscalera(vec2(-p2.x,-p2.y)-vec2(8.0,-40.0), vec2(10.0,10.0), 10.0 );
    float dinter3 = opBlock(dc2, p2.z, 5.);
    res = opU3(res, vec3(dinter3,101.0,-1.0));

    float dd2 = opScaleEscalera(vec2(p2.x,-p2.y)-vec2(8.0,-40.0), vec2(10.0,10.0), 10.0 );
    float dinter4 = opBlock(dd2, p2.z, 5.);
    res = opU3(res, vec3(dinter4,101.0,-1.0));

    return res;
}


vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*t;
        dS = GetDist(p);
        t += dS.x;
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
            {mObj.hitbln = true; minDist = abs(t); break;}
        if(t>MAX_DIST)
            {mObj.hitbln = false;    minDist = t;    break; } 
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return t;
}

float GetShadow(vec3 p, vec3 plig)
{   vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l , MAX_STEPS/2);
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {
        float hr = 0.01 + float(i) * 0.5 / 4.0;
        float dd = GetDist(nor * hr + pos).x;
        occ += (hr - dd)*sca;
        sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 col, float t) 
{   vec3 lightPos=lp;
    vec3 hit = ro + rd * t;
    vec3 norm = GetNormal(hit);
    vec3 light = lightPos - hit;
    float lightDist = max(length(light), .001);
    float atten = 1. / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    light /= lightDist;

    float occ = occlusion(hit, norm);

   float dif = clamp(dot(norm, light), 0.0, 1.0);
    dif = pow(dif, 4.) * 2.;
    float spe = pow(max(dot(reflect(-light, norm), -rd), 0.), 8.);
    vec3 color = col*(dif+.35 +vec3(.35,.45,.5)*spe) + vec3(.7,.9,1)*spe*spe;
    color*=occ;
    return color;
}

