        result+= Getluz( p,ro,rd, nor, colobj ,light_pos2,d)*light_color2;

        col= result;
         // Exponential distance fog
        col = mix(col, 0.8 * vec3(0.7, 0.8, 1.0), 0.5 - exp2(-0.0011 * d *d ));
        col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/5.0 ;
    }
    else if(d>MAX_DIST)
    col= render_sky_color(rd);
