vec2 rez;
vec2 fc;

vec3 fetch(int x, int y) {
    vec2 p = fc + vec2(x,y);
    return texture(iChannel0, p / rez).rgb;
}

float combine(vec2 p) {
    p = clamp(p, vec2(-1.0), vec2(1.0))*0.5+0.5;
    return 1.0 - max(p.x, p.y);
}

vec4 process(vec2 c) {
   rez = iChannelResolution[0].xy;
   fc = c;

    //vec3 p00 = fetch(-2,-2);
    vec3 p10 = fetch(-1,-2);
    vec3 p20 = fetch( 0,-2);
    vec3 p30 = fetch( 1,-2);
    //vec3 p40 = fetch( 2,-2);

    vec3 p01 = fetch(-2,-1);
    vec3 p11 = fetch(-1,-1);
    vec3 p21 = fetch( 0,-1);
    vec3 p31 = fetch( 1,-1);
    vec3 p41 = fetch( 2,-1);

    vec3 p02 = fetch(-2, 0);
    vec3 p12 = fetch(-1, 0);
    vec3 p22 = fetch( 0, 0);
    vec3 p32 = fetch( 1, 0);
    vec3 p42 = fetch( 2, 0);

    vec3 p03 = fetch(-2, 1);
    vec3 p13 = fetch(-1, 1);
    vec3 p23 = fetch( 0, 1);
    vec3 p33 = fetch( 1, 1);
    vec3 p43 = fetch( 2, 1);

    //vec3 p04 = fetch(-2, 2);
    vec3 p14 = fetch(-1, 2);
    vec3 p24 = fetch( 0, 2);
    vec3 p34 = fetch( 1, 2);
    //vec3 p44 = fetch( 2, 2);


    vec3 corners = p11 + p31 + p13 + p33;
    vec3 edges = p12 + p32 + p21 + p23;

    vec3 lp = corners + 4. * edges - 20.0 * p22;

    vec3 bilp = p01 + p02 + p03 + p14 + p24 + p34 + p43 + p42 + p41 + p30 + p20 + p10
        - 2. * corners - 10. * edges + 36.0 * p22;

    float D = 0.0;
    float dx = 1.0;
    vec2 d = sin(((fc/rez)*2.0-1.0)*radians(360.0)*1.0)*0.5+0.5;
    float dt = 0.16 / (max(0.0,2.0 - length(d))*float(R)/60.0);
    //bilp = bilp / (3.0 * dx * dx * dx * dx);
    //lp = lp / (6.0 * dx * dx);
    bilp = bilp / (3.0 * dx);
    lp = lp / (6.0 * dx);

    vec3 u = p22;        

    float H = 2.0;
    vec3 mu = mix(vec3(0.5), vec3(2.0), sin((iTime/120.0 + vec3(0,1,2)/3.)*radians(360.0))*0.5+0.5);
    vec3 beta = (vec3(combine(u.yz),combine(u.xz),combine(u.xy))*2.0-1.0)*0.5;

    vec3 du = mu*u - bilp - 2.*lp - u + beta*u*u - u*u*u;

    vec3 res = u + du*dt;

    // combined average
    float k = (res.x + res.y + res.z) / 3.0;

    return vec4(mix(res, vec3(k), vec3(0.01)),0.01);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 C = iMouse.xy / iResolution.xy;
    if (iFrame == 0) {
        vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;        
        uv.x *= iResolution.x / iResolution.y;
        float c0 = length(uv) - 0.5;
        float c1 = length(uv - vec2(0.0,0.25));
        float c2 = length(uv - vec2(0.0,-0.25));
        float d = max(min(max(c0, -uv.x),c1-0.25),-c2+0.25);        
        d = max(d, -c1+0.07);
        d = min(d, c2-0.07);
        d = min(d, abs(c0)-0.01);
        float w = sign(d) * sin(c0*80.0);
        vec3 h = pastelizer(atan(uv.y, uv.x)/radians(360.0))*2.0-1.0;
        fragColor = vec4(h*w,0.0);
    } else if ((iFrame % R) == 0) {
        vec2 c = vec2(fragCoord / iResolution.xy);
        fragColor = texture(iChannel0, (c - C) / 2.0 + C);
    } else {
        vec4 s = process(fragCoord);
        fragColor = clamp(s, -1.0, 1.0);
    }    
}

