void mainImage( out vec4 O, vec2 u )
{
    O = T(u);                                          // previous state
 // if ( iFrame%4 > 0 ) return;                        // to slow down
    if (u.y==20.5 && u.x<7.) {                         // === particles
       vec2 P = O.xy;                                  // prev location
       O.xy = P + O.zw;                                // particle move
       if ( T(O.xy).w != u.x || min(O.x,O.y)<0. || max(O.x,O.y)>20. ) // goes out of level:
           O.xy -= .5*O.zw,
           floor(P.x+O.z)-floor(P.x) == 0. ? O.w = - O.w : O.z = -O.z,// bounce
           O.xy += .5*O.zw;

       if (iFrame < 1) {                               // init partics
           vec2 D = sign(hash(u.x)-.5); 
           O = vec4(u.xx-.25*D.yx,.125*D);             // normal speed = .5*D
       }
    } 
    if( max(u.x,u.y) > 20.) return;                    // === terrain
    for( float i=.5; i<7.; i++ ) {                     // if we are where partic gone out of field
        vec4 P = T(vec2(i,20));
        if ( O.w != i && floor(u) == floor(P.xy+P.zw) ) O.w = i; // then extend field
