    d.x = max(d2.x,d.x);
    return vec3(d.x,d.yz+d2.yz);
}

float monogramShapeMask(vec2 p){
    p.y*=0.8;
    float d = length(p-vec2(0.025,0.0))-0.05;
    float d2 = length(p+vec2(0.025,0.0))-0.05;
    return max(d2,d);
}

vec3 monogramIcon1(vec2 p, vec3 col){
    p*=R(radians(-iTime*12.0));
    vec2 prevP = p;
    p*=R(radians(45.0));
    p = abs(p)-0.037;
    float d = length(p)-0.04;
    d = max(-(length(prevP)-0.025),d);
    p = prevP;
    vec3 mask = sdgCircle(p,0.11);
    mask.x = max(-d,mask.x);

    col = mix(col,vec3(mask.yz,1.)*BASE_COLOR*noise3d(vec3(p,1.0)*300.0)*1.25,S(prevP,mask.x,0.0));
    return col;
}

vec3 monogramIcon2(vec2 p, vec3 col){
    vec2 prevP = p;
    p.y = abs(p.y)-0.05;
    vec3 d = monogramShape1(p);
    d.x = max(-(length(prevP)-0.015),d.x);
    col = mix(col,vec3(d.yz,1.)*BASE_COLOR*noise3d(vec3(p,1.0)*300.0)*1.25,S(prevP,d.x,0.0));
    p = prevP;

    p.x = abs(p.x)-0.05;
    p*=R(radians(90.0));
    d = monogramShape1(p);

    d.x = max(-(length(prevP)-0.015),d.x);

    col = mix(col,vec3(d.yz,1.)*BASE_COLOR*noise3d(vec3(p,1.0)*300.0)*1.25,S(prevP,d.x,0.0));
    return col;
}

vec3 monogramIcon3(vec2 p, vec3 col){
    p*=R(radians(iTime*10.0));
    vec2 prevP = p;

    p.y = abs(p.y)-0.05;
    float maskD = monogramShapeMask(p);

    p = prevP;

    p.x = abs(p.x)-0.05;
    p*=R(radians(90.0));
    float d2 = monogramShapeMask(p);

    maskD = min(d2,maskD);

    p = prevP;
    p*=R(radians(45.0));
    vec3 d = sdgBox(p,vec2(0.1));
    p.y=abs(p.y)-0.38;
    float maskD2 = length(p)-0.3;
    d.x = max(-maskD2,d.x);
    p = prevP;
    p*=R(radians(45.0));
    p.x=abs(p.x)-0.38;
    maskD2 = length(p)-0.3;
    d.x = max(-maskD2,d.x);

    d.x = max(-maskD,d.x);

    col = mix(col,vec3(d.yz,0.5)*BASE_COLOR*noise3d(vec3(p,1.0)*300.0)*1.25,S(prevP,d.x,0.0));

    p = prevP;

    d = sdgCircle(p,0.015);
    col = mix(col,vec3(d.yz,0.5)*BASE_COLOR*noise3d(vec3(p,1.0)*300.0)*1.25,S(prevP,d.x,0.0));

    return col;
}

vec3 lvlogo(vec2 p, vec3 col){
    vec2 prevP = p;
    vec3 d = sdgBox(p-vec2(0.0,0.18),vec2(0.06,0.02));
    p*=R(radians(25.0));
    vec3 d2 = sdgBox(p-vec2(-0.065,0.0),vec2(0.025,0.15));

    d = sdgSmoothMin(d,d2,0.03);
    p = prevP;

    d2 = sdgBox(p-vec2(-0.02,-0.12),vec2(0.16,0.025));
    d = sdgSmoothMin(d,d2,0.03);

    p*=R(radians(25.0));
    d2 = sdgBox(p-vec2(0.16,-0.02),vec2(0.015,0.05));
    d = sdgSmoothMin(d,d2,0.03);
    p = prevP;

    p.x = abs(p.x)-0.135;
    d2 = sdgBox(p-vec2(0.0,0.23),vec2(0.05,0.02));
    d = sdgMin(d,d2);
    p = prevP;

    p*=R(radians(-25.0));
    d2 = sdgBox(p-vec2(-0.03,0.1),vec2(0.025,0.16));
    p = prevP;
    d = sdgSmoothMin(d,d2,0.015);

    p*=R(radians(25.0));
    d2 = sdgBox(p-vec2(0.03,0.1),vec2(0.015,0.16));
    d = sdgSmoothMin(d,d2,0.02);
    p = prevP;
    d.x = max(-B(p-vec2(0.0,-0.08),vec2(0.05,0.01)),d.x);

    col = mix(col,vec3(d.yz,0.5)*BASE_COLOR*noise3d(vec3(p,1.0)*300.0)*1.25,S(prevP,d.x,0.0));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    p.y+=iTime*0.1;

    vec2 prevP = p;

    vec3 col = vec3(0.5,0.2,0.1)*noise3d(vec3(p,p.x)*1000.0)*2.5;
    p.y-=0.4;
    p = mod(p,0.8)-0.4;
    col = monogramIcon1(p,col);
    p = prevP;
    p = mod(p,0.4)-0.2;
    col = monogramIcon2(p,col);
    p = prevP;
    p = mod(p,0.8)-0.4;
    col = monogramIcon3(p,col);
    p = prevP;
    p-=0.4;
    p = mod(p,0.8)-0.4;
    col = monogramIcon3(p,col);
    p = prevP;

    p-=0.8;
    p.x-=0.4;
    p.y+=0.04;
    p = mod(p,0.8)-0.4;
    p*=1.3;
    col = lvlogo(p,col);

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float f = 40. * 0.05;
    float cf = 1. / f;

    float p = SinMod(iTime, cf, 0., 1.);
