    m.w = m.x; //w records min(x,y,z)
    m.x=0.;//x records which pieces does this pix belong to
    if(m.y < m.w){m.w = m.y; m.x = .33;}
    if(m.z < m.w){m.w = m.z; m.x = .67;}
    m.w = min(m.w, m.w*(1. - m.w)*iResolution.y*w/(1. + .16/r));//AA



