HitInfo traceRay(in Ray ray) {
    HitInfo hit = HitInfo(-1.0, vec3(0.0), vec3(0.0), vec2(0.0), -1);

    // Ground and wall planes
    addRayHit(hit, planeIsect(ray, mat4(1.0), 1));
    addRayHit(hit, planeIsect(ray, Translate(vec3(0.0, 0.0, -10.0)) * RotateX(RHO), 1));

    // Big sphere light
    addRayHit(hit, sphereIsect(ray, Translate(vec3(0.0, 1.0, -5.0)), 2));

    // Cubes
    addRayHit(hit, cubeIsect(ray, Translate(vec3(-3.0, 1.0, -8.0)) * RotateY(1.8), 3));
    addRayHit(hit, cubeIsect(ray, Translate(vec3(3.0, 1.0, -6.0)) * RotateY(-1.5) * RotateX(-0.5) * RotateZ(2.0), 4));

    // Small spheres and medium sphere
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-3.0, 0.2, -5.0)) * Scale(vec3(0.2)), 5));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-1.0, 0.5, -4.0)) * Scale(vec3(0.5)), 6));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(3.0, 0.2, -5.0)) * Scale(vec3(0.2)), 7));

    // Bilinear patch
    addRayHit(hit, patchIsect(ray,
        vec3(0.8, 0.0, -4.25),
        vec3(2.8, 0.0, -3.25),
        vec3(0.75, 0.0, -2.75),
        vec3(0.8, 1.0, -2.25), 
    8));

    // Small sphere lights
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-3.25, 2.25, -8.0)) * Scale(vec3(0.25)), 9));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-1.5, 0.2, -2.8)) * Scale(vec3(0.2)), 10));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(1.25, 0.1, -2.25)) * Scale(vec3(0.1)), 11));

    return hit;
}

Material getMaterial(in vec2 surfUv, in int objId) {
    Material mtl = Material(vec3(0.0), vec3(0.0));

    // Ground and wall planes
    if (objId == 1) mtl = Material(vec3(1.0, 0.8, 0.5), vec3(0.0));

    // Big sphere light
    if (objId == 2) mtl = Material(vec3(0.0), vec3(2.0));

    // Boxes
    if (objId == 3) mtl = Material(vec3(1.0, 0.0, 0.0), vec3(0.0));
    if (objId == 4) mtl = Material(vec3(0.0, 1.0, 0.0), vec3(0.0));

    // Small spheres and medium sphere
    if (objId == 5) mtl = Material(vec3(1.0, 0.0, 0.0), vec3(0.0));
    if (objId == 6) mtl = Material(vec3(1.0, 1.0, 0.0), vec3(0.0));
    if (objId == 7) mtl = Material(vec3(0.0, 0.0, 1.0), vec3(0.0));

    // Bilinear patch
    if (objId == 8) mtl = Material(vec3(0.0, 0.8, 0.6), vec3(0.0));

    // Small sphere lights
    if (objId == 9) mtl = Material(vec3(0.0), vec3(0.0, 10.0, 0.0));
    if (objId == 10) mtl = Material(vec3(0.0), vec3(3.0, 0.6, 2.4));
    if (objId == 11) mtl = Material(vec3(0.0), vec3(5.0, 4.0, 1.0));

    return mtl;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    uint rngState = uint(uint(fragCoord.x) * uint(1973) + uint(fragCoord.y) * uint(9277) + uint(iFrame) * uint(26699)) | uint(1);

    // Load last frame
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);

    // Jitter UVs for antialiasing
    fragCoord += vec2(rand01(rngState), rand01(rngState));
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Camera ray
    mat3 cam = getCameraBasis(vec2(0.0));
    Ray ray = Ray(vec3(0.0, 1.0, 0.0), normalize(cam * vec3(uv, 1.0)));

    // Pathtrace!
    vec3 acc = vec3(0.0);
    vec3 throughput = vec3(1.0);
    for (int bounce=0; bounce < MAX_BOUNCES; bounce++) {
        HitInfo hit = traceRay(ray);
        if (hit.hitDist < 0.0) break;

        hit.surfNor = normalize(hit.surfNor); // Normalize the surface normal as a final step
        hit.surfNor *= sign(dot(hit.surfNor, -ray.dir)); // Flip towards the camera

        // Step to the surface and reflect randomly
        ray.pos = hit.hitPos + hit.surfNor * EPSILON;
        ray.dir = normalize(hit.surfNor + randUnitVec(rngState));        

        Material mtl = getMaterial(hit.surfUv, hit.objId);
        acc += mtl.emissive * throughput;
        throughput *= mtl.albedo;
    }

    // Accumulate
    fragColor.rgb += acc;
    fragColor.a += 1.0;

    // Restart on mouse down
    if (iMouse.z > 0.0) {
        fragColor = vec4(0.0);    
    }
}

