#define SHOW_EDGES false
#define SHOW_SHADOWS true

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

float sdBox( in vec3 p, in vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdf( in vec3 p )
{
    vec3 c = vec3(3);
    vec3 modPos = mod(p + .5 * c, c) - .5 * c;
    vec3 pos = vec3(modPos.x, p.y, modPos.z);
    pos = p;
    //return max(length(p - vec3(0, 0, 0)) - 1., length(p - vec3(1., 0., 1.3 * sin(iTime))) - 1.1);
    //p = mod(p + vec3(5), 10.) - vec3(5);
    //return sdBox(vec3(modPos.x, p.y, modPos.z), vec3(1));

    // Sphere moving through cube
    float d = min(
        sdBox(pos, vec3(1)),
        sdBox(pos - vec3(0, -2, 0), vec3(15, 1, 15))
    );

    d = max( d, -(length(vec3(pos.x, pos.y + sin(iTime / 2.), pos.z)) - 1.2) );
    for(int x=-1; x<=1; x++) for(int z=-1; z<=1; z++)
    {
        if ( x == 0 && z == 0) continue;
        d = max( d, -(length(vec3(pos.x + 3.4 * float(x), pos.y - sin(iTime / 2.), pos.z + 3.4 * float(z))) - 1.2) );
    }

    return d;
}

vec3 calcNormal( in vec3 p ) // for function f(p)
{
    const float h = 0.0001;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sdf( p + k.xyy*h ) + 
                      k.yyx*sdf( p + k.yyx*h ) + 
                      k.yxy*sdf( p + k.yxy*h ) + 
                      k.xxx*sdf( p + k.xxx*h ) );
}

float ray_march( in vec3 origin, in vec3 direction )
{

    vec3 pos = origin;

    // early escape if origin is inside sdf
    if ( sdf(pos) < 0. )
    {
        return -1.;
    }

    float dist = 0.;
    for (int i=0; i<100; i++)
    {
        float step_dist = sdf(pos);
        dist += step_dist;
        pos = origin + dist * direction;

        if ( step_dist < 0.0001)
        {
            return dist;
        }

    }

    return -1.;
}

bool is_edge( in vec3 position )
{
    vec3 p = vec3(0);

    float totalSdf = 0.;
    float possible_max = 0.;

    float scale = 0.015;
    float minVal = -1. * scale;
    float maxVal = 1. * scale;
    float stepSize = .5 * scale;
    for(p.x = minVal; p.x <= maxVal; p.x += stepSize)
    for(p.y = minVal; p.y <= maxVal; p.y += stepSize)
    for(p.z = minVal; p.z <= maxVal; p.z += stepSize)
    {
        if ( p == vec3(0.) ) continue;

        float weight = (abs(p.x) * abs(p.y) * abs(p.z));
        if ( sdf(position + p) > 0. ) totalSdf += weight;
        else totalSdf -= weight;

        possible_max += weight;

        //totalSdf += sdf(p);
    }

    totalSdf /= possible_max;
    if ( totalSdf > 0.5 ) return true;

    return false;
}

vec3 march_scene( in vec3 origin, in vec3 direction )
{
    float dist = ray_march(origin, direction);

    // No hit
    if ( dist == -1. )
    {
        // Calculate sky color
        //return vec3(0.5, 0.85, 0.9) + vec3(dot(direction, vec3(0, 1, 0) * 0.6));
        return vec3(.1);
    }

    vec3 pos = origin + direction * dist;

    vec3 lightdir = normalize(vec3(1, -.6, 1));
    vec3 lightcolor = vec3(1, .3, .8);
    vec3 color = vec3(1, 0, 0) * lightcolor * max(dot(-lightdir, calcNormal(pos)), 0.1);

    if ( SHOW_SHADOWS )
    {
        float dist_to_light = ray_march(pos + -vec3(20) * lightdir , lightdir);
        if ( dist_to_light < 20. - 0.001 ) 
        {
            color = vec3(.05);
        }
    }

    if (SHOW_EDGES && is_edge(pos) )
    {
        return vec3(0.34);
    }

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Camera setup
    vec2 uv = fragCoord.xy / vec2(iResolution.x,iResolution.y) * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv *= vec2(6, 6. / aspect);

    vec3 worldpos = vec3(uv.x, uv.y, 0.);
    worldpos -= vec3(0, 0, 20); // camera position
    vec3 worlddir = normalize(vec3(0, 0, 1));
    mat3 rot = rotateX(-0.4) * rotateY(0.4) * rotateZ(0.0);
    worldpos = rot * worldpos;
    worlddir = rot * worlddir;

    vec3 color = vec3(0);
    color = march_scene(worldpos, worlddir);

    fragColor = vec4( color, 1.0 );
}

#define BUMP 0.3

// dispersion amount
#define DISP_SCALE 0.3

// minimum IOR
#define MIN_IOR 1.1

// chromatic dispersion samples, higher values decrease banding
#define SAMPLES 9

// time scale
#define TIME 0.1*iTime

// sharpness of the sample weight distributions, higher values increase separation of colors
#define SHARP 15.0

#define FILMIC
#ifdef FILMIC
// tweaked version of a filmic curve from paniq with a softer left knee
vec3 contrast(vec3 x) {
    x=log(1.0+exp(x*10.0-7.2));
    return (x*(x*6.2+0.5))/(x*(x*6.2+1.7)+0.06);
}
#else
#define SIGMOID_CONTRAST 8.0
vec3 contrast(vec3 x) {
	return (1.0 / (1.0 + exp(-SIGMOID_CONTRAST * (x - 0.5))));    
}
#endif

vec3 sampleWeights(float i) {
	return vec3(exp(-SHARP*pow(i-0.25,2.0)), exp(-SHARP*pow(i-0.5,2.0)), exp(-SHARP*pow(i-0.75,2.0)));
}

mat3 cameraMatrix() {
    vec3 ro = vec3(sin(TIME),0.0,cos(TIME));
    vec3 ta = vec3(0,1.5,0);  
    vec3 w = normalize(ta - ro);
    vec3 u = normalize(cross(w,vec3(0,1,0)));
    vec3 v = normalize(cross(u,w));
    return mat3(u,v,w);
}

// same as the normal refract() but returns the coefficient
vec3 refractK(vec3 I, vec3 N, float eta, out float k) {
    k = max(0.0,1.0 - eta * eta * (1.0 - dot(N, I) * dot(N, I)));
    if (k <= 0.0)
        return vec3(0.0);
    else
        return eta * I - (eta * dot(N, I) + sqrt(k)) * N;
}

vec3 sampleDisp(vec2 uv, vec3 disp) {
	vec2 p = uv - 0.5;

    // camera movement
    mat3 camMat = cameraMatrix();

    vec3 rd = normz(camMat * vec3(p.xy, 1.0));
    vec3 norm = normz(camMat * disp);

    vec3 col = vec3(0);
    const float SD = 1.0 / float(SAMPLES);
    float wl = 0.0;
    vec3 denom = vec3(0);
    for(int i = 0; i < SAMPLES; i++) {
        vec3 sw = sampleWeights(wl);
        denom += sw;
        float k;
        vec3 refr = refractK(rd, norm, MIN_IOR + wl * DISP_SCALE, k);
        vec3 refl = reflect(rd, norm);
        col += sw * mix(texture(iChannel1, refl).xyz, texture(iChannel1, refr).xyz, k);
        wl  += SD;
    }

    return col / denom;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 texel = 1. / iResolution.xy;
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 n  = vec2(0.0, texel.y);
    vec2 e  = vec2(texel.x, 0.0);
    vec2 s  = vec2(0.0, -texel.y);
    vec2 w  = vec2(-texel.x, 0.0);

    float d   = texture(iChannel0, uv).x;
    // uncomment to just render the heightmap
    //#define SIMPLE
    #ifdef SIMPLE
    fragColor = 0.5+0.02*vec4(d);
    #else
    float d_n  = texture(iChannel0, (uv+n)  ).x;
    float d_e  = texture(iChannel0, (uv+e)  ).x;
    float d_s  = texture(iChannel0, (uv+s)  ).x;
    float d_w  = texture(iChannel0, (uv+w)  ).x; 
    float d_ne = texture(iChannel0, (uv+n+e)).x;
    float d_se = texture(iChannel0, (uv+s+e)).x;
    float d_sw = texture(iChannel0, (uv+s+w)).x;
    float d_nw = texture(iChannel0, (uv+n+w)).x; 

    float dxn[3];
    float dyn[3];
    float dcn[3];

    dcn[0] = 0.5;
    dcn[1] = 1.0; 
    dcn[2] = 0.5;

    dyn[0] = d_nw - d_sw;
    dyn[1] = d_n  - d_s; 
    dyn[2] = d_ne - d_se;

    dxn[0] = d_ne - d_nw; 
    dxn[1] = d_e  - d_w; 
    dxn[2] = d_se - d_sw; 

    // The section below is an antialiased version of 
    // Shane's Bumped Sinusoidal Warp shadertoy here:
    // https://www.shadertoy.com/view/4l2XWK
	#define SRC_DIST 8.0
    vec3 sp = vec3(uv-0.5, 0);
    vec3 light = vec3(cos(iTime/2.0)*0.5, sin(iTime/2.0)*0.5, -SRC_DIST);
    vec3 ld = light - sp;
    float lDist = max(length(ld), 0.001);
    ld /= lDist;
    float aDist = max(distance(vec3(light.xy,0),sp) , 0.001);
    float atten = min(0.07/(0.25 + aDist*0.5 + aDist*aDist*0.05), 1.);
    vec3 rd = normalize(vec3(uv - 0.5, 1.));

    float spec = 0.0;
	float den = 0.0;

    vec3 dispCol = vec3(0);

    // compute dispersion and specular with antialiasing
    vec3 avd = vec3(0);
    for(int i = 0; i < 3; i++) {
        for(int j = 0; j < 3; j++) {
            vec2 dxy = vec2(dxn[i], dyn[j]);
            float w = dcn[i] * dcn[j];
            vec3 bn = reflect(normalize(vec3(BUMP*dxy, -1.0)), vec3(0,1,0));
            avd += w * bn;
            den += w;
            dispCol += w * sampleDisp(uv, bn);
            spec += w * ggx(bn, vec3(0,1,0), ld, 0.3, 1.0);
        }
    }

    avd /= den;
    spec /= den;
    dispCol /= den;

    // end bumpmapping section

    fragColor =  vec4(contrast(.75*dispCol),1) + 1.0*vec4(0.9, 0.85, 0.8, 1)*spec;

    #endif

}

