    }

    vec3 du = texBlur(B,p).xyz + TIMESTEP * texBlur(A,p).xyz;
    // hard clamping
    //du = length(du) > 1.0 ?  normz(du) : du;
    // soft clamping
    du -= 0.005 * pow(length(du), 3.) * normz(du);
    r.xyz = mix(texture(B,p).xyz, du, 1.);
    //r.w = texBlur(A,p).w;
    r.w = texGradX(A,p).x + texGradY(A,p).y;
}

