#define PI 3.1415926536
#define TAU 6.2831853072

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

#define drawSDF(dist, col) color = max(color, col * smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 0.6;
    float unit = 1.2 / iResolution.y;
    vec3 color = vec3(0.0);

    float len = 2.0;
    float twist = sin(0.25 * iTime) * PI / 2.0;

    // Convert twist angle to a rotation matrix
    float co = cos(twist), si = sin(twist);
    mat2 rot = mat2(co, -si, si, co);

    // Calculate height to keep line length constant
    float chord = 2.0 * sin(twist);
    float halfHeight = sqrt(len * len - chord * chord) / 2.0;

    for (float a=0.0; a < TAU; a += TAU / 50.0) {
        vec3 p1 = vec3(cos(a), -halfHeight, sin(a));
        vec3 p2 = vec3(p1.x, halfHeight, p1.z);

        p1.xz *= transpose(rot); // Rotate in opposite direction
        p2.xz *= rot;

        // Project
        p1.xy /= p1.z + 5.0;
        p2.xy /= p2.z + 5.0;

        drawSDF(sdLine(uv, p1.xy, p2.xy), vec3(1.0));
    }

    fragColor = vec4(color, 1.0);
}

