float map (vec3 p) {
    float tri = sdTriPrism((p-tri_pos)*tri_mat,vec2(.2,.7));
    float rec = sdBox((p-rec_pos)*rec_mat,vec3(.1,.2,.3));
    float plane = sdPlane(p,vec3(0,0,1),0.001);
    float sphere = sdSphere((p-sph_pos)*sph_mat,.2);
    float ball = sdSphere((p-bal_pos)*bal_mat,.1);
    float torus = sdTorus((p-tor_pos)*tor_mat,vec2(.5,.05));
    float light = sdSphere(p-light_pos,.1);
    return min(tri,min(plane,min(rec,min(sphere,min(torus,min(light,ball))))));
