        if (t > 0.) color = vec4(1,.25,0,1);
        else color = vec4(.1,.1,.7,1);
        P = rec_mat[2];
        C = .3;
        J = vec3(0);
    }
    if (dsphere < d) {
        d = dsphere;
        color = vec4(0,.6,.8,1);
        C = -.5;
        J = vec3(0);
        P = vec3(0);
    }
    if (dlight < d) {
        d = dlight;
    }
    if (dtorus < d) {
        d = dtorus;
        color = vec4(1,.6,.0,1);
        J = 10.*cross(tor_mat[2],p-tor_pos);
        C = 0.;
        P = vec3(0);
    }
    if (dball < d) {
        d = dball;
        color = vec4(1,.2,.3,1);
        C = 2.;
        J = vec3(0);
        P = vec3(0);
    }
    if (d>1e-3) {
        color = vec4(0);
        P = vec3(0);
        C = 0.;
        J = vec3(0);
    }
}
vec3 normal (vec3 p) {
    vec2 e = vec2(1e-3,0);
    return normalize(vec3(
        map(p+e.xyy)-map(p-e.xyy),
        map(p+e.yxy)-map(p-e.yxy),
        map(p+e.yyx)-map(p-e.yyx)
    ));
}
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
vec3 rand(vec4 p) {
    vec4 h = hash44(p);
    float theta = 2. * pi * h.x;
    float phi = acos(1. - 2. * h.y);
    return vec3(sin(phi) * cos(theta),
                sin(phi) * sin(theta),
                cos(phi) );
}

