    float sf = dim.y / (iResolution.y - 64.);

    vec2 offset = 0. * vec2(0, sf * (24. * cos(0.8 * iTime) - 4.));
    vec2 coord = floor( sf * fragCoord + offset);
    //coord -= ceil(sf * 0.5 * iResolution.xy - 0.5 * dim); // idk if this is optimal

    vec3 A = texelFetch( iChannel0,  ivec2(coord) , 0 ).rgb;
    float B = texelFetch( iChannel1,  ivec2(coord) , 0 ).x;

    float d = length(coord + 0.5 - ceil(0.5 * dim)); //??   
   // if (coord.x >= dim.x || coord.y >= dim.y)
    //    B = 0.;

    float sd = step(d, 0.5 * dim.x);
    float sb = step(length(A), 0.);

    //B *= sd * sb;
    B = smoothstep(0., 1., B);
    B = smoothstep(0., 1., B);
    B *= 1. - coord.y / dim.y;

    B = ceil(5. * B) / 5.;
    vec3 e = vec3(0.8);//0.5 + 0.5 * thc(3., iTime));

    float diag = 0. * -0.2 * ceil(-2. * (coord.x + coord.y)/dim.x) / 2.;
    vec3 bcol = sd * sb * pal(diag + 0.38 * B, e, e, e, .5 * vec3(0.,0.33,0.66));

    //B = step(0.5, B);
    col += bcol;

  //  col 
    //e *= 0.15;
    col += step(0.001, A.y) * pal(diag + 0.5 * B, e, e, e, .5 * vec3(0., 0.33, 0.66));
    col += A.x;
    //col += (1.-A.x) * 0.1;
    col += B * (1.-sd);
    fragColor = vec4(col,1.0);
}

