float circle(vec2 fragCoord, vec2 dimm) {
    float k = ceil(0.5 * dimm.x);

    float d = length(fragCoord - 0.5 * dimm);
    float v = step(d, k);

    float v1 = v - step(length(fragCoord - vec2(1,0)  - 0.5 * dimm), k);
    float v2 = v - step(length(fragCoord - vec2(-1,0) - 0.5 * dimm), k);
    float v3 = v - step(length(fragCoord - vec2(0,1)  - 0.5 * dimm), k);
    float v4 = v - step(length(fragCoord - vec2(0,-1) - 0.5 * dimm), k);

    return max(max(v1, v2), max(v3, v4));
}

vec2 edge(vec2 p) {
    vec2 p2 = abs(p);
    if (p2.x < p2.y) return vec2((p.x < 0.) ? -1. : 1., 0);
    else             return vec2(0., (p.y < 0.) ? -1. : 1.);
}

float getInnerOutline(vec2 fragCoord, vec2 dimm) {
    float d = length(fragCoord - 0.5 * dimm);
    float v = step(d, ceil(0.5 * dimm.x));
    return v;
}

void mainImage( out vec4 fragColor, in vec2 coord )
{
    vec2 dim = dim(iTime);
    vec3 col = vec3(0.);
    if (coord.x < dim.x && coord.y < dim.y) {

       // outer circle
       float v = circle(coord, dim);

       // inner circle (based off outer circle, so they align)
       // inspired from here: https://www.youtube.com/watch?v=I8fmkLK1OKg
       vec2 c = abs(coord - 0.5 * dim); 
       vec2 dir;
       if (c.x <= c.y) dir = vec2(0, (coord.y <= 0.5 * dim.y) ? 1. : -1.);
       else            dir = vec2((coord.x <= 0.5 * dim.x) ? 1. : -1., 0);          
       float v2 = circle(coord - dir, dim);

       //v *= pow(coord.y / dim.y, 1.);
       v2 *= coord.y / dim.y;

       float st = 5.;
       v = ceil(st * v) / st;
       v2 = ceil(st * v2) / st;


       col = vec3(v, v2, 0);
    }


    fragColor = vec4(col,1.0);
}

