float circle(vec2 uv, vec2 position, float radius, float blur)
{
	return 1.0 - smoothstep(radius - blur, radius + blur, length(uv - position));
}

vec2 rotate(vec2 original, float angle, vec2 pivot)
{
	mat2 rotation = mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
	vec2 final = original;
	final -= pivot;
	final *= rotation;
	final += pivot;
	return final;
}

vec2 scale(vec2 original, vec2 scale, vec2 pivot)
{
	scale = 1.0 / scale;
	mat2 s = mat2(scale.x, 0.0, 0.0, scale.y);
	vec2 final = original;
	final -= pivot;
	final *= s;
	final += pivot;
	return final;
}

float changeRange(float iValue, float iMin, float iMax, float fMin, float fMax) 
{
	float fValue = (iValue-iMin)/(iMax-iMin);
	fValue *= fMax-fMin;
	fValue += fMin;
	return fValue;
}

int nCircles = 50;
bool coolEffect = false;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mr = min(iResolution.x, iResolution.y);
	vec2 uv = fragCoord / mr;

    uv.x -= 0.5;

    vec3 col = vec3(0);

	for (int i = 0; i < nCircles; i++) {
		float s = changeRange(float(i), 0.0, float(nCircles - 1), 1.0, 1.0/float(nCircles));

		vec2 suv = scale(uv, vec2(s), vec2(0.5));

		float t = changeRange(float(i), 0.0, float(nCircles - 1), iTime*0.1, iTime*2.1);

        if (coolEffect && i%2!=0) t *= -1.0;

		vec2 ruv = rotate(suv, t, vec2(0.5));

        if (distance(ruv, vec2(0.5)) < 0.5) {
            float c = float(ruv.x < 0.5);
            col = vec3(c);
        }
	}

    fragColor = vec4(col,1.0);
}

