float hexa_full(vec2 uv, float rad, float th)
{
    vec2 uv2 = uv;
    float rep = PI*2.0/6.;
    float an = atan(uv2.y,uv2.x);
    float sectors = mod(an+rep*.5,rep)-rep*.5;
    float id = floor((an+rep*.5)/rep);
    uv2 = vec2(sin(sectors), cos(sectors))*length(uv);
    if (mod(id, 2.) < 0.1)
        return abs(uv2.y-rad)-th;
    return 10.;
}

float hexa(vec2 uv, float rad, float th)
{
    vec2 uv2 = uv;
    float rep = PI*2.0/6.;
    float an = atan(uv2.y,uv2.x);
    float sectors = mod(an+rep*.5,rep)-rep*.5;
    uv2 = vec2(sin(sectors), cos(sectors))*length(uv);

    return abs(uv2.y-rad)-th;
}

vec3 palette(float f)
{
    vec3 cols[4];

    cols[0] = vec3(0.341,0.094,0.271);
    cols[1] = vec3(0.565,0.047,0.243);
    cols[2] = vec3(0.780,0.000,0.224);
    cols[3] = vec3(1.000,0.341,0.200);

    return cols[int(min(f*4.,3.))];
}

vec3 rdr(vec2 uv)
{
    float shp = iResolution.x*.5;
    vec3 col = palette(uv.x+.35);
    float sides = 6.;
    float h = .1;
    float angle = PI/sides;
    float len =  h*2.0*tan(PI/sides)*.5;
    float th = 0.004;

    vec3 yellow = vec3(1.000,0.765,0.000);

    col = mix(col, yellow, 1.-sat(_sqr((uv+vec2(len*2.55,-len*1.5))*r2d(angle), vec2(len, th))*shp));

    vec2 uv2 = uv;
    float rep = PI*2.0/sides;
    float an = atan(uv2.y,uv2.x);
    float sectors = mod(an+rep*.5,rep)-rep*.5;
    uv2 = vec2(sin(sectors), cos(sectors))*length(uv);



    float shape2 = hexa(uv, .1, th);
    shape2 = min(shape2, hexa_full(uv, .08,th));
    col = mix(col, yellow, 1.-sat(shape2*shp));

    uv += vec2(-len,0.)*3.5;
    float shape3 = hexa(uv, .1, th);

    shape3 = max(shape3, -uv.y+len);
    col = mix(col, yellow, 1.-sat(shape3*shp));

    uv += vec2(-len,0.)*3.5;
    float shape4 = hexa(uv, .1, th);
    col = mix(col, yellow, 1.-sat(shape4*shp));
    return col;
}

