vec2 map(vec3 p)
{
    vec2 acc = vec2(10000.,-1.);

    float mount = texture(iChannel0, p.xz*.01).x-.1-.05*sin(iTime+p.x+p.y);//*2.*(1.-sat(length(p.xz)-.5));

    for (int i = 0 ; i < 4; ++i)
    {
        vec3 pos = p+vec3(0.,-float(i)*.1,0.);
        float cur = max(mount-0.1*float(i), abs(pos.y)-.2);
        acc = _min(acc, vec2(cur, i));
    }
   //acc = _min(acc, vec2(length(p-vec3(1.5,0.,0.))-1., 0.));

    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.01, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps && distance(p, ro) < 15.; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.01)
            return vec3(res.x, distance(p, ro), res.y);
        p+=rd*res.x*.125;
    }
    return vec3(-1.);
}

vec3 rdr(vec2 uv)
{
    vec3 col = vec3(46, 41, 78)/255.;

    float d = 5.;
    float t = sin(iTime*0.1+1.57);
    float move = sin(iTime*.125)*10.;
    vec3 ro = vec3(sin(t)*d,-5.,cos(t)*d+move);
    vec3 ta = vec3(0.,0.,move);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv);
    vec3 res = trace(ro, rd, 256);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = n*.5+.5;
        vec3 lpos = vec3(-5.,-5.,5.);
        vec3 ldir = normalize(lpos-p);
        vec3 cols[4];
        cols[0] = vec3(84, 19, 136)/255.;
        cols[1] = vec3(241, 233, 218)/255.;
        cols[2] = vec3(255, 212, 0)/255.;
        cols[3] = vec3(	217, 3, 104)/255.;
        col = cols[int(res.z)]*sat(sat(dot(n,ldir))+.2);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    _seed = iTime+texture(iChannel0, uv).x;
    //vec2 off = .75*(vec2(rand(), rand())-.5)*2.*1./iResolution.x;
    vec3 col = rdr(uv);

    vec2 off = vec2(1., -1.)/(iResolution.x*1.5);

    if (true)//diff > 0.3) // Not so cheap antialiasing
    {
        //col = vec3(1.,0.,0.);
        vec3 acc = col;
        acc += rdr(uv+off.xx);
        acc += rdr(uv+off.xy);
        acc += rdr(uv+off.yy);
        acc += rdr(uv+off.yx);
        col = acc/5.;

    }
    col *= 1.9/(col+1.);
    //col = pow(col, vec3(.4545));
    col = mix(col, texture(iChannel1, fragCoord/iResolution.xy).xyz, .6);
    fragColor = vec4(col,1.0);
}

