}
vec2 _min(vec2 a, vec2 b)
{
    if (a.x < b.x)
        return a;
    return b;
}


vec2 map(vec3 p)
{
    vec3 pglass = p;
    vec2 acc = vec2(10000., -1.);
    acc = _min(acc, vec2(max(-p.y,-p.x), 0.));

    acc = _min(acc, vec2(max(p.y+5., -p.x-.5), 0.));

    float reppillar = 5.;

    vec3 ppillar = p;
    ppillar.xy *= r2d(.1);
    ppillar.z = mod(ppillar.z+reppillar*.5,reppillar)-reppillar;

    acc = _min(acc, vec2(length(ppillar.xz)-.3, 0.));

    acc = _min(acc, vec2(max(-p.z+100.,-p.x), 0.));

    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.001,0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}
vec3 getCam(vec3 rd, vec2 uv)
{
    float fov = 4.;
    vec3 r = normalize(cross(rd, vec3(0.,1.,0.)));
    vec3 u = normalize(cross(rd, r));
return normalize(rd+(r*uv.x+u*uv.y)*fov);
}

vec3 accLight;
vec3 trace(vec3 ro, vec3 rd, int steps, float maxDist)
{
    accLight = vec3(0.);
    vec3 p = ro;
    for (int i = 0; i < steps && distance(p, ro) < maxDist;++i)
    {
        vec2 res = map(p);
        if (res.x < 0.001)
            return vec3(res.x, distance(p, ro), res.y);
        /*if (res.y == FLAME_MAT)
        accLight += vec3(0.941,0.376,0.000)*(1.-sat(res.x/.05))*.05;*/
        p+= rd*res.x;
    }
    return vec3(-1.);
}

float getShadow(vec3 ro, vec3 lpos, int steps)
{
    float maxDist = distance(ro, lpos);
    vec3 rd = -normalize(lpos-ro);
    vec3 p = ro;
    for (int i = 0; i < steps && distance(p, ro) < maxDist;++i)
    {
        vec2 res = map(p);
        if (res.x < 0.001)
            return 0.;
        p+= rd*res.x;
    }
    return float(distance(p, ro) > maxDist);
}
vec3 getEnv(vec3 rd)
{
    return texture(iChannel2, rd*vec3(1.,-1.,1.)).xyz*.5*vec3(0.941,1.000,1.000)*1.7;//*.25;
    vec2 uv = vec2(atan(rd.z, rd.x)/PI, (acos(rd.y)/PI-.5)*2.);
    float stars = pow(texture(iChannel0, uv*5.*vec2(2.,1.)).x, 10.)*sat(-rd.y);
    vec3 back = mix(vec3(0.910,0.671,0.588), vec3(0.176,0.180,0.345)*.75, pow(sat(-rd.y*2.+.2), .3)); 
    back += vec3(0.455,0.871,0.925)*pow(sat(-(rd.x-2.1)*.4+rd.y*2.),1.2)*.5;
    back += vec3(0.733,0.416,0.208)*pow(sat(-(rd.x-1.9)*.4+rd.y*2.),3.);
    back *= 1.-sat(length((uv-vec2(.75,0.1))*.5*vec2(2.,2.)));
    return back+ vec3(1.)*stars+vec3(.2,.4,.7)*stars*1.;
}

vec3 getMat(vec3 res, vec3 rd, vec3 p, vec3 n)
{
    vec3 col = n*.5+.5;
    vec3 lpos = vec3(0.,5.,-5.)+1.*(vec3(rand(), rand(), rand())-.5);
    vec3 ldir= p-lpos;
    vec3 lcol = vec3(1.000,0.796,0.659);
    vec3 h = normalize(rd+ldir);
    vec3 ambient = vec3(0.);
    float ndotl = sat(dot(n, normalize(ldir)));
    vec3 albedo = vec3(.1,.1,.1);
    vec3 spec = vec3(1.,0.,0.);
    float gloss = 1.;
    if (res.z == GROUND_MAT)
    {
        albedo = texture(iChannel3, p.xz*.2*vec2(1.,3.)).xyz;
//        ambient = mix(ambient, vec3(1.000,0.298,0.220), sat((sin(length(p.xz)*20.)-.95)*400.));
    }
    if (abs(res.z) == BAT2_MAT)
    {
        ambient = vec3(1.000,0.298,0.220);
        if (res.z > 0.)
            ambient = ambient.zyx;
    }

    col = ambient;
    if (getShadow(p+n*0.01,  lpos, 256) > 0.5)
        col += albedo*pow(ndotl,1.)*2.*lcol;
/*    if (res.z == FLAME_MAT)
    {
        col = 2.*mix(vec3(1.000,0.749,0.322), vec3(1.,1.,1.), sat(-dot(rd, n)));
    }*/

    return col;
}

vec3 rdr(vec2 uv)
{

    vec3 col = vec3(0.);
    float xpos = sin(iTime*0.+.5)*2.2;
    float offy = -.85;
    float zoff = 5.9;
    //uv *= r2d(.25);
    vec3 dof = (vec3(rand(), rand(), rand())-.5)*.05*0.;
    vec3 ro = vec3(4.,-1.,-5.)+dof;
    vec3 ta = vec3(3.,2.,-2.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv)-dof*0.0025;
    vec3 res = trace(ro, rd, 4096, 500.);
    float maxdist = 20.;
    float dist = maxdist;
    if (res.y > 0.)
    {
        dist = res.y;
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = n*.5+.5;
        col = getMat(res, rd, p, n);
        col += accLight;
        if (true)
        {

            for (int i = 0; i < 2; ++i)
            {
                vec3 refl = normalize(reflect(rd, n));
                float spec = 1.;
                if (res.z == GLASS_MAT)
                    spec = .1;
                refl = mix(refl, normalize(vec3(rand(), rand(), rand())-.5), spec);

                if (dot(refl, n) < 0.01)
                    refl = -refl;
                vec3 resrefl = trace(p+n*0.01, refl, 2048, 15.);
                if (resrefl.y > 0.)
                {
                    vec3 prefl = p+refl*resrefl.y;
                    vec3 nrefl = getNorm(prefl, resrefl.x);
                    col += getMat(resrefl, refl, prefl, nrefl);
                    n = nrefl;
                    p = prefl;
                }
                else
                {
                    col += getEnv(refl);
                    break;
                }
                col += accLight;
                rd = refl;
            }


        }
    col = mix(col, vec3(0.), sat(exp(-dist*.05-.1)));
    }
    else
    {
        col = getEnv(rd);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    float scaleF = 1.;
    uv *= scaleF;
    seed=texture(iChannel0,uv).x;
    seed+=fract(iTime);
    vec2 off = (vec2(rand(), rand())-.5)*3.*vec2(1., -1.)/(iResolution.x*1.75/scaleF);
    vec3 col = rdr(uv+off);

    col = sat(col);
    col = pow(col, vec3(.9));

    if (iFrame > 1)
        col = mix(texture(iChannel1, fragCoord/iResolution.xy).xyz, col,  .05);
    ;//col = mix(texture(iChannel1, fragCoord/iResolution.xy).xyz, col,  1.0 / float(iFrame + 1));

    fragColor = vec4(col,1.0);
}

