vec3 artDeco(vec2 uv)
{
    vec3 col;
    vec3 yell = vec3(245, 209, 66)/255.;
    vec3 green = .7*vec3(33, 181, 156)/255.;
    col = vec3(0.15);
    //col = mix(green, yell, sat((sin(uv.x*155.)-.5)*400.));

    float rep = 0.1;

    float anim = mix(0.,.005, 0.*sin(iTime+(uv.x+uv.y)*5.)*.5+.5);
