vec2 map(vec3 p)
{
    vec2 acc = vec2(10000.,-1.);

    vec3 rep = vec3(7.);
    float cl = 2.5;
    p = clamp(p, -rep*cl, rep*cl);
    vec3 id = floor((p+rep*.5)/rep);
    p = mod(p+rep*.5,rep)-rep*.5;
    p.xy *= r2d(id.x+iTime*.5*sin(id.y));
    p.xz *= r2d(id.y+iTime*.5*sin(id.y));
    acc = _min(acc, vec2(_cucube(p, vec3(1.), vec3(.1)), 0.));

    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.001, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.001)
            return vec3(res.x, distance(p, ro), res.y);
        p+=rd*res.x;
    }
    return vec3(-1.);
}

vec3 rdr(vec2 uv)
{
    vec3 col = vec3(0.000,0.569,1.000)*(1.-sat(length(uv)));

    vec3 dof = normalize(vec3(rand()-.5, rand()-.5, 0.))*.1*mix(0.1,0.9, sat(sin(iTime)*.5+.5));
    vec3 ro = vec3(3.,-5.,-4.)+dof;
    vec3 ta = vec3(0.,0.,0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv)-dof*0.1;
    vec3 res = trace(ro, rd, 256);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = n*.5+.5;
        vec3 lpos = vec3(5.,-5.,5.);
        vec3 ldir = normalize(lpos-p);
        float ndotl = dot(ldir, n);
        col = vec3(0.996,0.675,0.125)*sat(ndotl)+vec3(0.996,0.690,0.165);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    _seed = iTime+texture(iChannel0, uv).x;
    vec3 col = rdr(uv);
    col = mix(col, texture(iChannel1, fragCoord/iResolution.xy).xyz, .9);
    fragColor = vec4(col,1.0);
