#define BAT_MAT 1.
float seed;
float rand()
{
    seed++;
    return hash11(seed);
}
mat2 r2d(float a) { float c= cos(a), s = sin(a); return mat2(c, -s, s, c); }
vec2 map(vec3 p)
{
    vec2 acc = vec2(10000.,-1.);

    float building = max(p.z, -_sqr(p.xy, vec2(3., .8)));
    acc = _min(acc, vec2(building, 0.));

    vec3 pfins = p-vec3(0.,0.,-.2);
    float repfins = 1.55;
    pfins.x = mod(pfins.x+repfins*.5, repfins)-repfins*.5;
    float fins = _cube(pfins, vec3(.01,1.,.2));
    acc = _min(acc, vec2(fins, 0.));


    // Outside
    acc = _min(acc, vec2(-p.z+1., BAT_MAT));
    //acc = _min(acc, vec2(length(p)-1., 0.));

    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.01, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps; ++i)
    {
        vec2 res = map(p);
