vec3 gradient(float f)
{
    vec3 cols[3];

    cols[0] = vec3(0.169,0.086,0.816);
    cols[1] = vec3(0.835,0.216,0.843);
    cols[2] = vec3(1.,1.,1.);

    float cnt = 2.;
    float cur = f*cnt;
    float curIdx = floor(cur);
    return mix(cols[int(curIdx)], cols[int(min(curIdx+1., cnt))], sat(fract(cur)));
}
vec3 gradient2(float f)
{
    vec3 cols[4];

    cols[0] = vec3(0.008,0.004,0.008);
    cols[1] = vec3(0.973,0.008,0.027);
    cols[2] = vec3(0.980,0.780,0.200);
    cols[3] = vec3(0.973,0.965,0.961);

    float cnt = 3.;
    float cur = f*cnt;
    float curIdx = floor(cur);
    return mix(cols[int(curIdx)], cols[int(min(curIdx+1., cnt))], sat(fract(cur)));
}
vec3 getEnv(vec3 rd)
{
    vec2 uv = vec2(atan(rd.z, rd.x), acos(rd.y));
    float stars = 2.5*pow(texture(iChannel0, uv*5.5).x, 5.)*sat(1.+sin(uv.y*5.2+uv.x*10.));
    vec3 grad = mix(
    vec3(0.067,0.086,0.125), 
    vec3(0.145,0.349,0.635), 
    pow(sat(sin((uv.x+uv.y)*10.)*.5+.5), .5));

    grad += .5*mix(
    vec3(0.110,0.149,0.122), 
    vec3(0.286,0.592,0.459), 
    pow(sat(sin((uv.x+uv.y)*5.)*.5+.5), .5))*.5*sat((uv.y-1.)*2.);

    //grad+= gradient(sat((-uv.y+1.6)*7.))*.3;

    grad += vec3(1.000,0.482,0.141)*(1.-sat(abs((uv.y-1.44+0.02*sin(uv.x+iTime*.25))*7.)));
    return stars*vec3(1.)+grad*sat(sat((-uv.y+1.8)*5.)+.2);
}

vec3 getMat(vec3 res, vec3 rd, vec3 p, vec3 n)
{
    vec3 col = n*.5+.5;
    col = vec3(.01);
    if (res.z == BAT_MAT)
    {
        //float bats = sat(_sqr(p.xy-vec2(0.,-1.5), vec2(2.))*100.);
        vec3 red = vec3(0.969,0.012,0.012);
        vec3 bats = vec3(0.);
        float mask = 10000.;
        p.x += -iTime*.05;
        p.x += iDate.x*100.+iDate.z;
        //p.x += float(int(iTime*.5));
        vec2 pa = p.xy-vec2(0.,.5);
        float repa = 1.3;
        float ida = floor((pa.x+repa*.5)/repa); 
        pa += vec2(.1*sin(ida*11.),.2*sin(ida*15.));
        pa.x = mod(pa.x+repa*.5,repa)-repa*.5;

        float batA = _sqr(pa, vec2(.4+.2*sin(ida*2.),1.5)*.5);
        bats = mix(bats, 2.*gradient2(sat(pa.y*1.+.25)), (1.-sat(batA*400.)));
        mask = min(mask, batA);

        vec2 pb = p.xy-vec2(0.4,.5);
        float repb = .7;
        float idb = floor((pb.x+repb*.5)/repa); 
        pb += vec2(.2*sin(idb*11.),.2*sin(idb*15.));
        pb.x = mod(pb.x+repb*.5,repb)-repb*.5;

        float batB = _sqr(pb, vec2(.4+.1*sin(idb*2.),2.5)*.5);
        batB = max(batB, _sqr(pb*r2d(.5*idb), vec2(1.,.6)));
        vec3 colB = gradient2(sat(pb.y*.75+.5));
        colB = mix(colB.yzx, colB.yyz, sat(sin(idb*100.)*10.));

        bats = mix(bats, sat((sin(pb.x*20.)+.5)*100.)*2.*colB, (1.-sat(batB*400.)));
        mask = min(mask, batB);

        if (mask > 0.)
            col = getEnv(rd)*sat(rd.y*5.+1.5);
        else
            col = bats;
    }
    return col;
}

vec3 rdr(vec2 uv)
{
    vec3 col = vec3(0.);
        vec3 dof = (vec3(rand(), rand(), rand())-.5)*.05;
    vec3 ro = vec3(0.,0.,-7.)+dof;
    vec3 ta = vec3(0.,0.,0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv)-dof*0.005;
    vec3 res = trace(ro, rd, 256);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = getMat(res, rd, p, n);
        if (p.y > -.78 && res.z != BAT_MAT)
        {
            vec3 refl = normalize(reflect(rd, n)+(vec3(rand(), rand(), rand())-.5)*.5*vec3(.05,1.,0.05));
            vec3 resrefl = trace(p+n*0.01,refl, 128);
            if (resrefl.y > 0.)
            {
                vec3 prefl = p+refl*resrefl.y;
                vec3 nrefl = getNorm(prefl, resrefl.x);

                col += getMat(resrefl, refl, prefl, nrefl)*2.;
            }
            else
                col += getEnv(refl)*.125;
        }
    }
    else
        col = getEnv(rd);
    col += .1*rand();
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    seed = texture(iChannel0, uv).x+iTime;
    vec2 off = (vec2(rand(), rand())-.5)*3.*vec2(1., -1.)/(iResolution.x*1.75);
    vec3 col = rdr(uv+off);
    col += vec3(0.302,0.102,0.129)*(1.-sat(length(uv)+rand()));
    col += vec3(0.102,0.302,0.224)*(1.-sat(length(uv)-.5+rand()))*.1;
    col *= vec3(0.784,0.976,0.859);
    col = mix(col, texture(iChannel1, fragCoord/iResolution.xy).xyz, .95);
    fragColor = vec4(col,1.0);
}

