mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}


float GetDist(vec3 p) {
    float r1 = 0.75;
    float r2 = 0.45;
    float a = atan(p.x, p.z);
   // p.xz += 0.5 * cos(a + iTime);
    float d1 = length(p.xz) - r1;

    vec2 uv = vec2(d1, p.y);
    float av = atan(uv.x, uv.y);
    float rv = length(uv); // <----- change back to length(uv)
    //uv = vec2(2. * av, rv;
    float thing = 1.5 * av + 3.5 * p.y;
    //rv = mix(rv, log(2.8 * rv), 0.5 + 0.5 * thc(3.,iTime));
    rv = rv * (1.2 + 0.2 * cos(20. * rv));
    rv = mix(rv, uv.x, 0.5 + 0.5 * thc(3.,0.5 * iTime));
    uv = rv * vec2(cos(thing), sin(thing));

    uv *= Rot(a - iTime);
    uv.y = abs(uv.y) - 0.3;
    //uv.y += 0.1 * cos(a + iTime);
    //uv.y += 0.1 * cos(2. * a + iTime);
    float d2 = length(uv) - r2;

    return 0.3 * d2; //0.3
}

float RayMarch(vec3 ro, vec3 rd, float side) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p)*side;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

