#define pi 3.14159
vec4 load(float x, float y){return texture(iChannel0,vec2(x+.5,y+.5)/rez.xy);}
float tube(vec2 pa, vec2 ba){return length(pa-ba*clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0));}
void mainImage(out vec4 O, in vec2 U){
  O=texture(iChannel0,U/rez.xy);
  U=floor(U);
  vec4 L=load(0.,0.);
  float f=float(iFrame),mf=900.;//increase this to complete circuits after 180. secs
  if(mod(f,mf)==0.){
    if(U.x+U.y==0.)O=vec4(rez.x,0.,-(2.*U.x-rez.x)/rez.y*15.,0.); 
    else if(U.x>rez.x/2. || iFrame==0)O=vec4(1);
    else O=load(U.x+floor(rez.x/2.),U.y);
    return;
  }
  vec2 nv=vec2(cos(L.y),sin(L.y));
  if(U.x+U.y==0.){//logic pixel
    float s=floor(f/mf);
    f=mod(f-1.,mf);
    if(f>800.+s*12.)return;//approximately 1 loop has finished
    float f2=s*3.+200.;//199 for a new batch, every 3rd doodle is crap so step over it
    O.zw+=nv;
    O.y=mod(O.y+pi/f2*pow(f,2.),pi*2.);//increases the angle by pi*(step#^2)/doodle#
  }else{//draw
    vec2 p=L.zw,u=30.*(2.*U-rez.xy)/rez.y;
    float d=tube(u-p,nv);d=smoothstep(0.0,90./rez.y,d);
    O=min(O,vec4(d));
  }
}

