#define ortho(D)   vec2( -(D).y, (D).x )              // vec ortho to D
#define S(D)       ortho(D) / max( dot(D,D) , 1e-3 )  // irrotational swirl

void mainImage( out vec4 O, vec2 u )
{
    O = T(u);                                         // previous state
    if ( u== vec2(.5) )                               // previous mouse pos
      { if ( T(0).xy != iMouse.xy ) O = vec4(iMouse.xy,T(0).xy); return; } 

    vec2 U = u / R.y,
         M = iMouse.xy / R.y,
         d = .1* normalize( ortho( iMouse.xy - T(0).zw +1e-5 )), // swirl pair axis
         V = S(U-M+d) - S(U-M-d);                     // swirl pair irrotational field

    if (iMouse.z > 0.)                                // if click, swirl
        O = texture( iChannel0, ( U + .1*V/60. ) *R.y/R );

    if ( iFrame < 1 ) O = vec4(u/R,0,0);              // init uv field
}

