vec3 wood (vec2 u) {
    return texture(iChannel3,u).xyz;
}
vec3 stone (vec2 u) {
    return texture(iChannel2,u).xyz;
}
vec3 woodN (vec2 u) {
    float n = wood(u+vec2(0,1)/R).x;
    float e = wood(u+vec2(1,0)/R).x;
    float s = wood(u-vec2(0,1)/R).x;
    float w = wood(u-vec2(1,0)/R).x;
    return normalize(vec3(e-w,n-s,.5));
}
vec3 stoneN (vec2 u) {
    float n = stone(u+vec2(0,1)/R).x;
    float e = stone(u+vec2(1,0)/R).x;
    float s = stone(u-vec2(0,1)/R).x;
    float w = stone(u-vec2(1,0)/R).x;
    return normalize(vec3(e-w,n-s,.2));
}
void drawTop (vec3 p, vec3 d, top b, inout vec3 normal, inout vec3 color, int i) {
    vec3 n = vec3(0,1,0)*b.o;
    float dn = dot(d,n);
    float pn = dot(p-b.p,n);
    float pd = dot(p-b.p,d);
    float r = .05;
    float q = quadratic(
        1.-dn*dn,
        2.*pd-2.*pn*dn,
        dot(p-b.p,p-b.p)-pn*pn-r*r
    ).x;
    float qs = sphere(p,d,b.p,.4);
    vec3 i1 = p + d*q;
    float x1 = dot(i1-b.p,n);
    if (x1>+.5) q = sphere(p,d,b.p+.5*n,r);
    if (x1<-.5) q = sphere(p,d,b.p-.5*n,r);
    normal = normalize(p+d*q-(b.p+n*clamp(x1,-.5,.5)));
    if (q<0.||(qs<q&&qs>0.)) {
        q = qs;
        normal = normalize(p+d*q-b.p);
        vec3 no = b.o*normal;
        vec2 u = 2./pi*vec2(atan(length(no.xz),no.y),atan(no.z,no.x));

        color = .6*wood(u)*(.7+.6*sin(float(i)+vec3(1,2,3)));
        vec3 wn = woodN(u);
        normal = normal*wn.z+5.*wn.y*cross(normal,b.o[1])+1.*wn.x*cross(normal,b.o[0]);
    } else color = vec3(0);
}
void scene (inout vec3 p, inout vec3 d, inout vec3 normal, inout vec3 color) {
    float mi = 1e9;
    top object;
    forM(i) { // tops
        top t = get(i);
        float ii = iTop(p,d,t);
        if (ii < mi && ii>0.) {
            mi = ii;
            object = t;
            drawTop(p,d,t,normal,color,i);
        }
    }
    { // floor
        float ii = plane(p,d,vec3(0,-1.5,0),vec3(0,1,0));
        if (ii < mi && ii>0.) {
            mi = ii;
            vec3 u = (p + d*mi);
            float s = .6+.4*sign(sin(3.*u.x)*sin(3.*u.z));
            color = s*wood(s+.6*u.xz);
            vec3 wn =  woodN(s+.6*u.xz);
            wn.z *= .2+s;
            normal = normalize(wn).xzy;
        }
    }
    { // wall
        float ii = plane(p,d,vec3(0,0,1.5),vec3(0,0,1));
        if (ii < mi && ii>0.) {
            mi = ii;
            vec3 u = (p + d*mi);
            float s = .9-sin(2.*u.x);
            normal = stoneN(-.3*u.yx);
            color = .8*stone(-.3*u.yx);
            if (s<0.) {
                color = exp(-.1*abs(u.y))*sin(1.+.15*(u.y)*(1.+vec3(1,2,3)));
                normal = woodN(u.xy);
            }else if (s<.1) {
                color = .3*wood(.2*u.yx);
                normal = woodN(.2*u.yx);
            }
        }
    }

    p = p + d*mi;
    d = reflect(d,normal);
    p += 1e-4*d;
}
