   float c = cos(a), t = 1.-c;
   float s = sin(a);
   return mat3(
       t*o.x*o.x+c,t*o.x*o.y-o.z*s,t*o.x*o.z+o.y*s,
       t*o.x*o.y+o.z*s,t*o.y*o.y+c,t*o.y*o.z-o.x*s,
       t*o.x*o.z-o.y*s,t*o.y*o.z+o.x*s,t*o.z*o.z+c

   );

}
vec2 quadratic (float a, float b, float c) {
    float d = b*b-4.*a*c;
    if ( d < 0. ) return vec2(-1);
    return .5/a*vec2(-b-sqrt(d),-b+sqrt(d));
}
float plane (vec3 p, vec3 d, vec3 c, vec3 n) {
