// This buffer's red channel holds the SDF for the drawing,
// and the blue channel holds information about the type
// of surface. Whenever the user draws a line segment, the
// minimum between the current SD and the SD
// between the pixel and segment is stored. 

const float INFINITY = 1e10;

bool key_pressed(float key_code)
{
    return texture(iChannel1, vec2((key_code + 0.5) / 256.0, 0.5 / 3.0)).r > 0.0;
}

float line_sdf(vec2 p, vec2 a, vec2 b)
{
    if (a == b)
    {
        return distance(a, p);
    }

	vec2 ab = b - a;
	vec2 ap = p - a;
	float h = clamp(dot(ap, ab) / dot(ab, ab), 0.0, 1.0);
	return distance(h * ab, ap);
}

vec2 new_sd(vec2 p)
{
    // reset the SDF texture
    if (iFrame == 0 || key_pressed(KEY_RESET))
    {
        return vec2(INFINITY);
    }

    // the current SD
    vec2 sd = texture(iChannel0, p / iResolution.xy).rg;

    // if currently drawing
    if (iMouse.z > 0.0 && (key_pressed(KEY_DIFFUSE) || key_pressed(KEY_LIGHT) || key_pressed(KEY_GLASS) || key_pressed(KEY_MIRROR)))
    {
        // get previous frame's mouse location if it's valid
        vec2 prev_mouse = texture(iChannel0, vec2(0.5) / iResolution.xy).ba;
        prev_mouse = prev_mouse == vec2(-1.0) ? iMouse.xy : prev_mouse;

        // the new SD is the minimum of the SD to the line segment
        // and the current
        float ld = line_sdf(p, prev_mouse, iMouse.xy) - BRUSH_RADIUS * iResolution.y;
        if (ld < max(sd.r, 0.0))
        {
            sd.r = ld;
            if (key_pressed(KEY_DIFFUSE)) sd.g = float(DIFFUSE);
            if (key_pressed(KEY_LIGHT)) sd.g = float(EMISSIVE);
            if (key_pressed(KEY_GLASS)) sd.g = float(GLASS);
            if (key_pressed(KEY_MIRROR)) sd.g = float(MIRROR);
        }
    }

    return sd;
}

void mainImage(out vec4 frag_color, vec2 frag_coord)
{
    // store the SD in the red channel
    frag_color.rg = new_sd(frag_coord);

    // store the mouse position for the next frame in the
    // blue and alpha channels of the lower left pixel if
    // it is currently pressed
    if (frag_coord == vec2(0.5))
    {
        if (iMouse.z > 0.0)
        {
            frag_color.ba = iMouse.xy;
        }
        else
        {
            frag_color.ba = vec2(-1.0);
        }
    }
}

