// This buffer ray traces against the SDF from buffer A.
// It accumulates samples and stores the number of
// samples so far in the alpha channel.

const float PI = 3.141592564;

bool key_pressed(float key_code)
{
    return texture(iChannel2, vec2((key_code + 0.5) / 256.0, 0.5 / 3.0)).r > 0.0;
}

// gamma correct colors
const vec3 diffuse_color = pow(DIFFUSE_COLOR, vec3(GAMMA));
const vec3 emissive_color = pow(EMISSIVE_COLOR, vec3(GAMMA));
const vec3 glass_color = pow(GLASS_COLOR, vec3(GAMMA));
const vec3 mirror_color = pow(MIRROR_COLOR, vec3(GAMMA));
const vec3 sky_light = pow(SKY_LIGHT, vec3(GAMMA));

int seed;

float rand()
{
    int n = (seed++ << 13) ^ seed;
    return float((n * (n * n * 15731 + 789221) + 1376312589) & 0x7fffffff) / 2147483647.0;
}

float rand(int seed)
{
    int n = (seed++ << 13) ^ seed;
    return float((n * (n * n * 15731 + 789221) + 1376312589) & 0x7fffffff) / 2147483647.0;
}

void set_seed(vec2 p)
{
    seed = int(p.x + p.y * iResolution.x);
	seed = int(rand() * 2147483647.0) + iFrame;
}

vec2 sdf(vec2 p)
{
    return texture(iChannel0, p / iResolution.xy).rg;
}

vec2 grad(vec2 p)
{
    const vec2 e = vec2(1.0, 0.0);
    return vec2(
        sdf(p + e.xy).x - sdf(p - e.xy).x, 
        sdf(p + e.yx).x - sdf(p - e.yx).x
    ) / 2.0;
}

struct Mat
{
    int type;
    vec3 c; // color
    float e; // emissivity
};

Mat mat(vec2 p)
{
    float s = sdf(floor(p) + 0.5).y;
    Mat m;
    m.type = int(s);
    m.c =
        m.type == DIFFUSE  ? diffuse_color  :
        m.type == EMISSIVE ? emissive_color :
        m.type == GLASS    ? glass_color    : 
        m.type == MIRROR   ? mirror_color   :
        vec3(0.0);
    m.e = m.type == EMISSIVE ? EMISSIVITY : 0.0;
    m.type = m.type == EMISSIVE ? DIFFUSE : m.type;
    return m;
}

float ray_march(vec2 o, vec2 d)
{
    const float EPSILON = 0.1;
    float t = 0.0;
    vec2 p = o;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        float dist = abs(sdf(p).x);

        if (EPSILON < t && dist < EPSILON)
        {
            return t;
        }

        t += dist;
        p = o + d * t;

        if (p.x < 0.0 || iResolution.x <= p.x || p.y < 0.0 || iResolution.y <= p.y)
        {
            break;
        }
    }

    return -1.0;
}

struct Hit 
{
    vec2 p; // position
    vec2 n; // normal
    Mat m; // material
};

Hit hit(vec2 o, vec2 d)
{
    float t = ray_march(o, d);

    if (t == -1.0)
    {
        return Hit(vec2(0.0), vec2(0.0), Mat(0, vec3(0.0), 0.0));
    }

    Hit h;
    h.p = o + d * t;
    h.n = normalize(grad(h.p));
    h.m = mat(h.p);
    return h;
}


vec2 sample_semicircle(vec2 n, float t)
{
    t *= 2.0 * PI;
    vec2 s = vec2(cos(t), sin(t));
    return s * sign(dot(s, n));
}

vec3 trace(vec2 o, vec2 d)
{    
    vec3 final_color = vec3(0.0);
    vec3 multiplier = vec3(1.0);

    // handle case when ray origin is inside drawing
    if (sdf(o).x < 0.0)
    {
        Mat m = mat(o);
        switch (m.type)
        {
            case MIRROR:
            case DIFFUSE:
            {
                float t = ray_march(o, d);
                o += d * (t + 1.0);
                multiplier *= m.c;
                final_color += multiplier * m.e;
                break;
            }
        }
    }

    for (int i = 0; i < NUM_BOUNCES; ++i)
    {
        Hit h = hit(o, d);

        if (h.n == vec2(0.0))
        {
            final_color += multiplier * sky_light;
            break;
        }

        h.n *= -sign(dot(h.n, d));
        multiplier *= h.m.c;
        final_color += multiplier * h.m.e;

        switch (h.m.type)
        {
            case DIFFUSE:
            {
                o = h.p;
                d = sample_semicircle(h.n, rand());
                multiplier *= dot(d, h.n);
                break;
            }

            case GLASS:
            {                
                // i think this is incorrect :c
                float n1 = 1.0;
                float n2 = GLASS_IR;

                if (sdf(h.p).x < 0.0)
                {
                    n1 = GLASS_IR;
                    n2 = 1.0;
                }

                float eta = n1 / n2;
                float c1 = -dot(h.n, d);
                float k = 1.0 - eta * eta * (1.0 - c1 * c1);

                if (k < 0.0)
                {
                    o = h.p;
                    d = reflect(d, h.n);

                    break;
                }

                float c2 = sqrt(k);
                float rs = (n1 * c1 - n2 * c2) / (n1 * c1 + n2 * c2);

                if (rand() < rs)
                {
                    o = h.p;
                    d = reflect(d, h.n);

                    break;
                }

                o = h.p;
                d = eta * d - (eta * -c1 + c2) * h.n;
                o += d;

                break;
            }

            case MIRROR:
            {
                o = h.p;
                d = reflect(d, h.n);
                break;
            }
        }
    }

    return final_color;
}

void mainImage(out vec4 frag_color, vec2 frag_coord)
{
    set_seed(frag_coord);

    vec3 result = vec3(0.0);

    // accumulate samples by tracing rays in all directions
    for (int i = 0; i < NUM_SAMPLES + min(0, iFrame); ++i)
    {
        float blue_noise = fract(
            texture(iChannel3, frag_coord / iChannelResolution[3].xy).r +
            fract(float(iFrame * NUM_SAMPLES + i) * 0.61803399)
        );
        float r = blue_noise;
        //r = rand();
        float a = 2.0 * PI * (float(i) + r) / float(NUM_SAMPLES);
        vec2 o = floor(frag_coord) + vec2(rand(), rand());
        vec2 d = vec2(cos(a), sin(a));
        result += trace(o, d);
    }

    result /= float(NUM_SAMPLES);
    frag_color = vec4(result, 1.0);

    // accumulate samples from previous frame if 
    // scene hasn't changed. alpha channel keeps
    // track of how many frames have been accumulated
    if (iMouse.z <= 0.0 && !key_pressed(KEY_RESET))
    {
        vec4 t = texture(iChannel1, frag_coord / iResolution.xy);

        if (t.a >= 1e10)
        {
            frag_color = t;
        }
        else
        {
            frag_color += t;
        }
    }
}

