    }
    fragColor = vec4(vec3(c-0.02)*300.,1.0);
}

//The floor shows the internal distance/gradient
//Norm is scaled into 0-1 range for visualization to verify signs

//Set to 1 to show numeric gradient to verify that it matches analytic
#define SHOW_NUMERIC_GRADIENT 0

//if 1 it scales norm into 0-1, if 0 it takes abs
#define SHOW_FULL_NORM_RANGE 1

//Signed distance to an octahedon pyramid along with its analytic gradient
//This is formed from and octahedron for the upper part, and a box(rotated 45 degrees) for the bottom
//After simplifying things it ended up as this
//Octahedron Distance is from TheTurk's comment in https://www.shadertoy.com/view/wsSGDG
float sdgOctahedronPyramid(vec3 inP, float s, out vec3 norm) {    ;
    vec3 gradient;
    float m=0.0;

    if(inP.z >= 0.0){  //Octahedron for top
        vec3 p = vec3(abs(inP.xy), inP.z);
        m = (p.x + p.y + p.z - s) / 3.0;
        vec3 o = p - m;
        vec3 k = min(o, 0.0);
        o = o + (k.x + k.y + k.z) * 0.5 - k * 1.5;
        o = clamp(o, 0.0, s);     //our point on surface
        gradient = (p - o);
        gradient.xy *= sign(inP.xy);//fix signs for gradient(don't need z, as we use box in z < 0.0

    }else{//Box part for bottom
        vec2 pb = vec2(inP.x + inP.y, inP.x - inP.y);//rotate by 45 degrees to match octahedron
        vec2 boxG = max(abs(pb) - s, 0.0);//2D box clamp

        //if all you want is distance you could skip the next two lines(but you would instead have to scale boxG by 1/sqrt(2))
        boxG = boxG * sign(pb);     //Need to restore the signs for gradient
        boxG = vec2((boxG.x + boxG.y )* .5,(boxG.x - boxG.y )* .5);//Undo the 45 degree rotation, also only needed for gradient

        gradient =vec3(boxG,inP.z);
    }

    float d2 = dot(gradient,gradient);
    float d2abs = d2;
    if(m < 0.0){  //If we are inside the oct, need to fix up bottom plane & invert distance and gradient
        float z2 = inP.z*inP.z;
        d2abs = min(d2,z2);//add internal bottom plane for oct
        gradient = z2 < d2 ? vec3(0.0,0.0,-inP.z): -gradient;//apply bottom plane gradient if it was nearest
        d2 = -d2abs;
    }
    float d2Rsqrt = inversesqrt(d2abs);
    norm = gradient*d2Rsqrt;//normalize the gradient
    return d2*d2Rsqrt;
}


