#define GROUND_MAT 0.
#define ZERO_MAT 1.
#define TWO_MAT 2.
#define DOT_MAT 3.
#define CAKE_MAT 4.
#define CANDLE_MAT 5.
#define CHOCO_MAT 6.
#define CREAM_MAT 7.
#define FLAME_MAT 8.

float seed;
float rand()
{
    seed++;
    return hash11(seed);
}

vec2 _candle(vec3 p)
{
    float h = .3;
    float w = .04;
    float curvyness = 18.; 
    p.xz *= r2d(p.y*curvyness);
    float a = atan(p.z, p.x);
    float height = abs(p.y+h)-h;
    float top = length(p.xz)-w*pow(sat((p.y+h*2.)*20.), 2.);
    float curve = (sin(a*4.));
    float curvenext = (sin(a*4.+.1));
    float shape = max(max(length(p.xz)-w-w*.1*curve, height), top);
    return vec2(shape, (p.y < -h*2.+.05 ? 1. : sign(curve-curvenext)));
}

float _cream(vec3 p)
{

    float f = -p.y*2.; 
    float pa = .3;
    float pb = 1.8;
    float shp = 400.;
    float w = pow(sat(sin(f)*.5+.5), pa)*sat(f*shp)*sat((-f+PI*.5)*shp)
    +pow(sat(sin(f)*.5+.5),pb)*sat((f-PI*.5)*shp)*sat((-f+PI)*shp);
    float l = length(p.xz);
    p.xz *= r2d(p.y*.5);
    float an = atan(p.z, p.x)-p.y+(PI*.5)*3.;

    float trails = (sin(an*20.));
    float shape = l+.2-w-.03*trails*sat(f*2.);//w;//.5-.1*trails;//(.5-.1*trails);//*w;
    return shape;
}

vec2 map(vec3 p)
{

    vec3 op = p;
    //p.x-= -.7-1.8;
    float th = .1;
    vec2 acc = vec2(1000.,-1.);
    vec3 pg = p;
    pg.yz *= r2d(.5);
    acc = _min(acc, vec2(smin(-p.y+.5, -abs(pg.z)+10.0, 1.8), GROUND_MAT));
    acc = _min(acc, vec2(smin(-p.y+.5, -abs(pg.x)+10.0, 1.8), GROUND_MAT));

    float cake = sdRoundedCylinder(p, .8,.08,.5);

    acc = _min(acc, vec2(cake, CAKE_MAT));

    acc = _min(acc, vec2(sdRoundedCylinder(p, .825,.04,.0125), CHOCO_MAT)); // layer

    vec3 pbtn = p-vec3(0.,0.,0.);
    float repy = .2;
    float idy = floor((pbtn.y+repy*.5)/repy);

    pbtn.y = mod(pbtn.y+repy*.5,repy)-repy*.5;
    float stpbtn = PI*2./20.;
    float abtn = atan(pbtn.z, pbtn.x)+idy;
    float ida = floor((abtn+stpbtn*.5)/stpbtn);
    float bbtn = mod(abtn+stpbtn*.5,stpbtn)-stpbtn*.5;
    pbtn.xz = vec2(sin(bbtn), cos(bbtn))*length(pbtn.xz);

    float btn = length((pbtn-vec3(0.,0.,1.625))*vec3(1.,1.,2.))-.07;
    if (abs(idy) < 3. && idy != 0.)
        acc = _min(acc, vec2(btn, 10.+mod(ida, 2.)));

    vec3 pcream = p;

    float stpa = PI*2./20.;
    float a = atan(p.z, p.x);
    float b = mod(a+stpa*.5,stpa)-stpa*.5;
    p.xz = vec2(sin(b), cos(b))*length(p.xz);

    vec2 candle = _candle(p-vec3(0.0,-.5,.8));
    acc = _min(acc, vec2(candle.x, CANDLE_MAT*candle.y));

    float flame = length((p-vec3(0.,-1.15,0.8))*vec3(1.,.5,1.))-.01;
    acc = _min(acc, vec2(flame, FLAME_MAT));
        float stpcream = PI*2./10.;
    float acream = atan(pcream.z, pcream.x);
    float bcream = mod(acream+stpcream*.5,stpcream)-stpcream*.5;
    pcream.xz = vec2(sin(bcream), cos(bcream))*length(pcream.xz);
    float cream = _cream((pcream-vec3(0.,-0.5,1.3))*5.);
    acc = _min(acc, vec2(cream, CREAM_MAT));

    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.001,0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 accLight;
vec3 trace(vec3 ro, vec3 rd, int steps)
{
    accLight = vec3(0.);
    vec3 p = ro;
    for (int i = 0; i < steps;++i)
    {
        vec2 res = map(p);
        if (res.x < 0.001)
            return vec3(res.x, distance(p, ro), res.y);
        if (res.y == FLAME_MAT)
        accLight += vec3(0.941,0.376,0.000)*(1.-sat(res.x/.05))*.1;
        p+= rd*res.x*.125;
    }
    return vec3(-1.);
}

vec3 getEnv(vec3 rd)
{
    return texture(iChannel2, rd*vec3(1.,-1.,1.)).xyz*.25;
    vec2 uv = vec2(atan(rd.z, rd.x)/PI, (acos(rd.y)/PI-.5)*2.);
    float stars = pow(texture(iChannel0, uv*5.*vec2(2.,1.)).x, 10.)*sat(-rd.y);
    vec3 back = mix(vec3(0.910,0.671,0.588), vec3(0.176,0.180,0.345)*.75, pow(sat(-rd.y*2.+.2), .3)); 
    back += vec3(0.455,0.871,0.925)*pow(sat(-(rd.x-2.1)*.4+rd.y*2.),1.2)*.5;
    back += vec3(0.733,0.416,0.208)*pow(sat(-(rd.x-1.9)*.4+rd.y*2.),3.);
    back *= 1.-sat(length((uv-vec2(.75,0.1))*.5*vec2(2.,2.)));
    return back+ vec3(1.)*stars+vec3(.2,.4,.7)*stars*1.;
}

vec3 getMat(vec3 res, vec3 rd, vec3 p, vec3 n)
{
    vec3 col = n*.5+.5;
    vec3 lpos = vec3(-0.,5.,5.);
    vec3 ldir= p-lpos;
    vec3 h = normalize(rd+ldir);
    vec3 ambient = vec3(0.);
    float ndotl = sat(dot(n, normalize(ldir)));
    vec3 albedo = vec3(.1,.1,.1);
    vec3 spec = vec3(1.,0.,0.);
    float gloss = 1.;
    if (res.z == GROUND_MAT)
    {
        albedo = mix(vec3(.1), vec3(0.000,0.616,0.875), .25)*.5;
    }
    if (res.z == CAKE_MAT)
    {
        albedo = vec3(0.875,0.486,0.165);
    }
    if (res.z == CHOCO_MAT)
    {
        albedo = vec3(.1,.1,.1);
    }
    if (abs(res.z) == CANDLE_MAT)
    {
        ambient = vec3(0.518,0.400,0.322)*.25;
        albedo = mix(vec3(0.494,0.333,0.243), vec3(0.875,0.784,0.851), sat(res.z));
    }
    if (res.z == CREAM_MAT)
    {
        ambient = vec3(0.518,0.400,0.322)*.5; 
        albedo = vec3(1.000,0.871,0.800)*.5;
    }
    if (res.z > 10.5)
    {
        albedo = vec3(1.000,0.871,0.800);
    }

    col = ambient+albedo*ndotl;
    if (res.z == FLAME_MAT)
    {
        col = 2.*mix(vec3(1.000,0.749,0.322), vec3(1.,1.,1.), sat(-dot(rd, n)));
    }

    return col;
}

vec3 rdr(vec2 uv)
{
    vec3 col = vec3(0.);
    float xpos = sin(iTime*0.+.5)*2.2;
    float offy = -.85;
    float d = 6.;
    float t = 0.*iTime*.125+2.;
    vec3 ro = vec3(sin(t)*d,-2.,cos(t)*d);
    vec3 ta = vec3(0.,0.,0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv);
    vec3 res = trace(ro, rd, 1024);

    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        col = n*.5+.5;
        col = getMat(res, rd, p, n);
        col += accLight;
        if (true)
        {
            //n+= normalize(vec3(rand(), rand(), rand())-.5)*.05*
            //(sin(p.x*10.+iTime+p.z*5.)+sin(p.z+iTime));
            //n = normalize(n);
            vec3 refl = normalize(reflect(rd, n));
            refl.x = -abs(refl.x);
            //float tex = texture(iChannel2, clamp(p.xz*vec2(-1.,1.5)*.25-vec2(.5,-0.5), vec2(-1.), vec2(0))-vec2(0.)).x;

            refl = mix(refl, normalize(vec3(rand(), rand(), rand())-.5), (1.-float(res.z > 9.)*.75));
            //refl = normalize(vec3(rand(), rand(), rand())-.5);

            if (dot(refl, n) < 0.01)
                refl = -refl;
            vec3 resrefl = trace(p+n*0.01, refl, 128);
            if (resrefl.y > 0.)
            {
                vec3 prefl = p+refl*resrefl.y;
                vec3 nrefl = getNorm(prefl, resrefl.x);
                col += getMat(resrefl, refl, prefl, nrefl);
            }
            else
                col += getEnv(refl)*1.5;
            col += accLight;
            //col += tex*.3*vec3(.5,.1,.12);
            //col *= sat(tex+.75);
        }
    }
    else
    {
        col = getEnv(rd);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    float scaleF = 1.;
    uv *= scaleF;
    //uv.y += 1.;
    seed=texture(iChannel0,uv).x;
    seed+=fract(iTime);
    vec2 off = (vec2(rand(), rand())-.5)*3.*vec2(1., -1.)/(iResolution.x*1.75/scaleF);
    vec3 col = rdr(uv+off);
    col *= 1.8/(col+1.);
    col = sat(col);
    col = pow(col, vec3(1.2));

    if (iFrame > 1)
        col = mix(texture(iChannel1, fragCoord/iResolution.xy).xyz, col, 1.0 / float(iFrame + 1));
        //    col = mix(texture(iChannel1, fragCoord/iResolution.xy).xyz, col, .3);

    fragColor = vec4(col,1.0);
}

