// Line SDF from iq.
float udSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 ba = b-a;
    vec2 pa = p-a;
    float h =clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length(pa-h*ba);
}

// Hashes from Dave Hoskins
float hash11(float p)
{
    p += 1.5;
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
float hash12(vec2 p)
{
    p += 0.2512;
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
vec4 hash42(vec2 p)
{
    p += 1.;
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);

}

vec4 valueNoise(vec2 t, float w){
    vec2 fr = fract(t);
    vec2 fl = floor(t);
	return 
        mix(
            mix( 
                hash42(vec2(fl.x, fl.y)),
                hash42(vec2(fl.x, fl.y + 1.)),
            	smoothstep(0.,1.,fr.y)
            ),
            mix( 
                hash42(vec2(fl.x + 1.,fl.y)),
                hash42(vec2(fl.x + 1.,fl.y + 1.)),
            	smoothstep(0.,1.,fr.y)
            ),
        smoothstep(0.,1.,pow(fr.x, abs(w)))
    );
}

// space repetition
#define pmod(p,a) mod(p,a) - 0.5*a

// rotation
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

