        if (dot(n, vn) < 0.0) {
           //continue;
        }

        //p2 *= 1.3;

        float t;
        vec3 p3 = ray_plane_isect(p2, n, p, vn, t);

        if (t < 0.0 || t > tt) {
            continue;
        }

        vec3 tan = -normalize(p2);

        p3 -= p2;

        float u = dot(p3, tan);
        float v = p3[1];
        float len = length(vec2(u, v));

        float lmax = 0.15;
