{



    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float maxJitter = 0.06;

    float duration = 0.3;

    float colorRoffset = 0.01;

    float colorBoffset = -0.025;

    float time = mod(iTime, duration * 2.0);

    float amplitude = max(sin(time * (PI / duration)) , 0.);

    float jitter = rand(uv.y) * 2.0 - 1.0;

    bool needOffset = abs(jitter) < maxJitter * amplitude;

    float textureX = uv.x + (needOffset ? jitter : (jitter * amplitude * 0.006));

    vec2 newTexture = vec2(textureX, uv.y);

    vec4 mask = texture(iChannel0, newTexture);

    vec4 maskR = texture(iChannel0, newTexture + vec2(colorRoffset * amplitude, 0.0));

    vec4 maskB = texture(iChannel0, newTexture + vec2(colorBoffset * amplitude, 0.0));

    // Output to screen
    fragColor = vec4(maskR.r, mask.g,maskR.b, 1.0 );
}

float scale = 6.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //划分网格
    vec2 id = floor(uv * scale);

    vec2 offs = vec2(sin(uv.x * scale + sin(iTime * 3.) * 20. + id.x * 0.2),
                     sin(uv.y * scale + sin(iTime * 3.) * 20. + id.y * 0.2));

    offs *= 0.01;

    uv.xy += offs;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}

//指定线宽
float eps = 0.05;

float plot(vec2 st, float pct){ 
    return smoothstep(pct - eps, pct, st.y) - smoothstep(pct, pct+eps, st.y );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;

    uv *= 5.0;
    //坐标转化为-2.5到2.5

    float y = uv.x;
    //y坐标转化为-1到1
    y = abs(2.0* fract(y) - 1.0);

    float a = plot(uv, y);

    // Output to screen
    fragColor = vec4(a, 0.2, 0.5, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy; 
    float gris = 1.0;
    float divisor = 4.8 / iResolution.y;
    float grosorInicial = divisor * 0.2;

    const int kNumPatrones = 6;

    //x:角的正弦，y:角的余弦，z:亮度因子
    vec3 datosPatron[kNumPatrones];
    //datosPatron[0] = vec3(-0.7071, 0.7071, 1.0); // -45°
    datosPatron[1] = vec3(0.0, 1.0, 0.6); // 0°
    //datosPatron[2] = vec3(0.0, 1.0, 0.5); // 0°
    //datosPatron[3] = vec3(1.0, 0.0, 0.4); // 90°
    //datosPatron[4] = vec3(1.0, 0.0, 0.3); // 90°
    //datosPatron[5] = vec3(0.0, 1.0, 0.2); // 0°

    vec4 color = texture(iChannel0, uv);
    fragColor = color;

    for (int i = 0; i < kNumPatrones; i++)
    {
        float sinv = datosPatron[i].x;
        float cosv = datosPatron[i].y;

        // @note 对 uv 坐标进行旋转
        vec2 rotuv = vec2(
                         uv.x * cosv - uv.y * sinv,
                         uv.x * sinv + uv.y * cosv
                     );

        //增加对比度
        float grosor = grosorInicial * float(i + 1);
        //构造不同方向的 “木刻纹”
        float dist = mod(rotuv.y - sin(rotuv.x * 20.0) * 0.03, divisor); 
        //灰度
        float lum = dot(vec3(.299, .578, .114), color.rgb);
        //将各个亮度分级的 “木刻纹” 进行融合
        if (lum < 0.82 - .12 * float(i)) //亮度分级
        {
            //增加对比
            float k = datosPatron[i].z;
            float x = (grosor - dist) / grosor;
            float fx = abs(x / k);
            gris = min(gris, fx);
        }
    }

    fragColor = vec4(gris, gris, gris, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float progress = mod(iTime, 0.7) / 0.7; // 重复0~1
    //透明度0~0.4
    float alpha = 0.4 * (1.0 - progress);
    //以中心为原点同频率（透明度）缩放
    float scale = 1.0 + 0.8 * progress;

    float weakX = 0.5 + (uv.x - 0.5) / scale;
    float weakY = 0.5 + (uv.y - 0.5) / scale;
    vec2 weakTextureCoords = vec2(weakX, weakY);

    vec4 weakMask = texture(iChannel0, weakTextureCoords);

    vec4 mask = texture(iChannel0, uv);

    // Output to screen
    fragColor = mask*(1.0 - alpha) + weakMask*alpha;
}

// playing with some shaping ideas 01/02/2022

vec2 gm;

vec2 smoothRot(vec2 p,float s,float m,float c,float d)
{
  s*=0.5;
  float k=length(p);
  float x=asin(sin(atan(p.x,p.y)*s)*(1.0-m))*k;
  float ds=k*s;
  float y=mix(ds,2.0*ds-sqrt(x*x+ds*ds),c);
  return vec2(x/s,y/s-d);
}

float wedge(vec3 position, float halfWidth, float halfHeight, float halfDepth)
{
    vec2 end = vec2(halfWidth, -halfHeight);
    vec2 d1 = vec2(-halfWidth - position.x, max(abs(position.y) - halfHeight, 0.0));
    vec2 d2 = position.xy - end * clamp(dot(position.xy, end) / dot(end, end), -1.0, 1.0);
    vec2 d3 = vec2(max(abs(position.x) - halfWidth, 0.0), -halfHeight - position.y); 
    float d4 = sqrt(min(min(dot(d1, d1), dot(d2, d2)), dot(d3, d3))) * sign(max(max(max(d1.x, d2.x), d2.y), d3.y));
    float d5 = abs(position.z) - halfDepth;
    return length(max(vec2(d4, d5), 0.0)) + min(max(d4, d5), 0.0);
}

mat2 rot(float a)
{
    float s=sin(a),c=cos(a);
    return mat2(c,s,-s,c);
}

float map( in vec3 p )
{

    float sides = 10.0;
    if (iMouse.z>0.5)
    {
        if (gm.y>0.5)
            p.y = abs(p.y);

        sides=6.0;
    }
    float h = 1.5+sin(iTime)*0.25;

    vec3 pos = p;
    pos.xz *= rot(iTime*0.5);
    pos.zx = smoothRot(pos.xz,sides,0.0,2.0,1.0);
    float d1 = wedge(pos, 1.0, h, 4.0);

    pos = p+vec3(5.0,0.0,0.0);
    pos.xz *= rot(iTime*0.5);
    pos.zx = smoothRot(pos.xz,sides,0.0,1.0,1.0);
    float d2 = wedge(pos, 1.0, h, 4.0);

    pos = p-vec3(5.0,0.0,0.0);
    pos.xz *= rot(iTime*0.5);
    pos.zx = smoothRot(pos.xz,sides,0.05,1.0,1.0);
    float d3 = wedge(pos, 1.0, h, 4.0);


    d1 = min(d1,d2);
    d1 = min(d1,d3);

    return d1*0.9;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

#define AA 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    gm = (-iResolution.xy + 2.0*iMouse.xy)/iResolution.y;    

     // camera movement	
	float an = 3.14*0.5;//0.5*iTime;
	vec3 ro = vec3( 8.0*cos(an), 0.0+(sin(iTime*0.25)*3.0), 8.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));



    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

        // raymarch
        const float tmax = 50.0;
        float t = 0.0;
        for( int i=0; i<128; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }

        // shading/lighting
        float v = 1.0-abs(p.y);
        vec3 col = vec3(v*0.1);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.7,0.6,0.4)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,0.8,0.6));
            col = vec3(0.2,0.3,0.4)*amb + vec3(0.8,0.7,0.5)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   

    vec2 uv = fragCoord/iResolution.xy;     
    vec2 c = (fragCoord -.5 * iResolution.xy)/iResolution.y;
    vec2 m = (iMouse.xy -.5 * iResolution.xy)/iResolution.y;

    vec2 dl = c;
    dl.x += 0.5 * cos(iTime / 2.);
    dl.y += 0.3 * sin(iTime * 2.);

    if (iMouse.z > 0.) dl = c-m;

    float dot = smoothstep(1.8/iResolution.y, .0, length(dl) - 0.1);

    vec4 mask = vec4(dot);
    mask *= texture(iChannel0, uv);            

    fragColor = mask;
}

