// Gold Noise ©2015 dcerisano@standard3d.com
// - based on the Golden Ratio
// - uniform normalized distribution
// - fastest static noise generator function (also runs at low precision)
// - use with indicated seeding method. 

float PHI = 1.61803398874989484820459;  // Φ = Golden Ratio   

float gold_noise(in vec2 xy, in float seed){
       return fract(tan(distance(xy*PHI, xy)*seed)*xy.x);
}

// -----------------------------------------------------------------------------

// The probability that a texel at the border (or inside the brush) will be live
#define EMERGE_PROP 0.25

#define RND_CELL(pos) vec4(step(gold_noise(pos, iTime + 0.1), EMERGE_PROP))

#define BRUSH_RADIUS 10.0

#define SHIFT_ASCII 16
#define CTRL_ASCII 17
#define SPACE_ASCII 32
#define IS_PRESSED 0
#define JUST_PRESSED 1

#define KEY(ascii, event) (texelFetch(iChannel1, ivec2(ascii, event), 0).x != 0.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Press Space to bring the Apocalypse.
    if(KEY(SPACE_ASCII, JUST_PRESSED))
    {
        fragColor = vec4(0.0);
        return;
    }

    ivec2 coord = ivec2(fragCoord);
    ivec2 resolution = ivec2(iResolution.xy);

    if(coord.y == 0 || coord.y == resolution.y - 1 ||coord.x == 0 || coord.x == resolution.x - 1){
        fragColor = RND_CELL(fragCoord);
        return;
    }

    // Only add/kill cells if CTRL is not pressed. This was added to allow saving an image with drawing cells.
    if(!KEY(CTRL_ASCII, IS_PRESSED) && iMouse.z > 0.0 && iMouse.w < 0.0 && distance(iMouse.xy, fragCoord) <= BRUSH_RADIUS){
        if(KEY(SHIFT_ASCII, IS_PRESSED)) // If SHIFT is pressed, kill all cells within brush radius
        {
            fragColor = vec4(0.0);
            return;
        } 
        else 
        {
            fragColor = RND_CELL(fragCoord);
            return;
        }
    }


    const int NEIGHBOR_COUNT = 8;

    const ivec2 NEIGHBORS[NEIGHBOR_COUNT] = ivec2[NEIGHBOR_COUNT](
        ivec2(-1, -1), ivec2( 0, -1), ivec2( 1, -1),
        ivec2(-1,  0),                ivec2( 1,  0),
        ivec2(-1,  1), ivec2( 0,  1), ivec2( 1,  1)
    );

    int count = 0;
    for(int index = 0; index < NEIGHBOR_COUNT; index++)
    {
        count += int(texelFetch(iChannel0, coord + NEIGHBORS[index], 0).r != 0.0);
    }

    bool result = texelFetch(iChannel0, coord, 0).r != 0.0;
    if(result)
    { // Alive
        if(count < 2 || count > 3) result = false; // Death due to under- or over-population
    } 
    else 
    { // Dead
        if(count == 3) result = true; // Birth of a new cell
    }

    fragColor = vec4(result);

}

