// Inspired by https://youtu.be/NCpaaLkmXI8


// Better not modify these 6, will probably mess up things
// Constants
#define PI 3.1415926535
#define TAU 2.0 * PI

// Ray Marching
#define EPSILON 0.001
#define MAX_STEPS 250
#define MAX_DIST 1000.0
#define MAX_INTERNAL_REFLECTIONS 10


// Modify these
// vvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvv
#define ZOOM 1.0
#define DISTANCE 5.0
#define ROTATION_SPEED 0.5

// My implementation of chromatic aberration (CA) is multiplicative (instead of additive)
// which indicates that 1.0 means no aberration and values greater than 1.0 create the
// rainbow-like effect of CA. Values in (0.0; 1.0) invert the order of the rainbow colors.
// Negative values don't make sense I guess since the ratio of the refractive indices, eta,
// would then also be negative
#define CHROMATIC_ABERRATION 1.025
#define COLOR vec3(0.0, 1.0, 1.0)
//#define DARKER_WHEN_THICKER

// These are the refractive indices
#define AIR 1.000293
#define GLASS 1.52
// ^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^


float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

// https://iquilezles.org/articles/distfunctions/
float sdRoundBox(vec3 p, vec3 b, float r)
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float GetDist(vec3 p)
{
    float minDist = 1e20;

    //float sphere1 = sdSphere(p, 0.5);
    float roundBox1 = sdRoundBox(p, vec3(1.0, 1.0 ,1.0), 0.333);
    //minDist = max(roundBox1, -sphere1);
    minDist = roundBox1;

    return minDist;
}


vec3 GetNormal(vec3 p)
{
    //return normalize(cross(dFdx(p), dFdy(p)));  // Works best for voxels

    float d = GetDist(p);
    vec2 e = vec2(EPSILON, 0);
    return normalize(d - vec3(GetDist(p - e.xyy),
                              GetDist(p - e.yxy),
                              GetDist(p - e.yyx)));
}


float RayMarch(vec3 ro, vec3 rd, float side)
{
    float dO = 0.0;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + rd * dO;
        float dS = side * GetDist(p);
        dO += dS;
        if (abs(dS) <= EPSILON || dO > MAX_DIST) break;
    }

    return dO;
}

// Correction offset so that RayMarch does not return immediately after starting to march
// Add it to the ray origin for reflection
// Subtract it from the ray origin for refraction
vec3 offset(vec3 n)
{
    return EPSILON * 2.0 * n;
}

bool TIR(vec3 refraction)
{
    //return dot(refraction, refraction) == 0.;

    // Probably faster because the entire expression will be false 
    // if it encounters one false expression
    return refraction.x == 0. && refraction.y == 0. && refraction.z == 0.;
}

vec4 RayMarchRefraction(vec3 pOut, vec3 rd, vec3 nOut, float eta, vec4 color)
{
    float etaEnter = eta;  // AIR / GLASS
    float etaExit = 1.0 / eta;  // GLASS / AIR

    vec3 refrDirEnter = refract(rd, nOut, etaEnter);

    // We need to march for the refraction since we need to know the point of intersection
    pOut -= offset(nOut);
    float dIn = RayMarch(pOut, refrDirEnter, -1.0);
    float thickness = dIn;

    // We do not need to check for anything, since we are guaranteed to hit the object
    // again when we start to march in the interior of the object
    vec3 pIn = pOut + refrDirEnter * dIn;
    vec3 nIn = -GetNormal(pIn);
    vec3 refrDirExit = refract(refrDirEnter, nIn, etaExit);

    // TIR -> Total Internal Reflection
    for (int i = 0; TIR(refrDirExit) && i < MAX_INTERNAL_REFLECTIONS; i++)
    {
        refrDirEnter = reflect(refrDirEnter, nIn);
        pIn += offset(nIn);
        dIn = RayMarch(pIn, refrDirEnter, -1.0);
        pIn += refrDirEnter * dIn;
        nIn = -GetNormal(pIn);
        refrDirExit = refract(refrDirEnter, nIn, etaExit);
        thickness += dIn;
    }

#ifndef DARKER_WHEN_THICKER
    thickness = 1.0;
#endif

    return color * texture(iChannel0, refrDirExit) / thickness;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / min(iResolution.x, iResolution.y);
    vec2 mouse = iMouse.xy / iResolution.xy;

    vec3 ro;

    if (iMouse.z > 0.0) 
    {
        float yaw = TAU * mouse.x;
        float pitch = PI * mouse.y;

        float cy = cos(yaw);
        float sy = sin(yaw);
        float cp = cos(pitch);
        float sp = sin(pitch);

        ro = DISTANCE * vec3(cy, cp, sy);
        ro.xz *= sp;  // Adjusting X and Z position accordingly when changing the pitch
    }
    else
    {
        float x = iTime * ROTATION_SPEED;
        float c = cos(x);
        float s = sin(x);
        vec3 cs0 = vec3(c, s, 0);
        ro = DISTANCE * cs0.xyy;
    }

    vec3 lookAt = vec3(0),
         f = normalize(lookAt - ro),
         r = normalize(cross(f, vec3(0,1,0))),
         u = cross(r, f),
         c = ro + f * ZOOM,
         i = c + uv.x * r + uv.y * u,
         rd = normalize(i - ro);

    vec4 col;
    float dOut = RayMarch(ro, rd, 1.0);

    if (dOut <= MAX_DIST)
    {
        vec3 pOut = ro + rd * dOut;
        //float distOut = GetDist(pOut);

        // We don't need to check for distOut <= EPSILON because the only other thing that
        // could've possibly happened when dOut <= MAX_DIST is that we ran out of steps which
        // is an indicator that we've very likely hit or got very close to hitting an object

        vec3 nOut = GetNormal(pOut);
        vec3 reflDir = reflect(rd, nOut);

        // We don't need to march again for the reflection since a box/sphere
        // cannot reflect itself - at least not from the outside
        vec4 reflCol = texture(iChannel0, reflDir);

        float eta = AIR / GLASS;

        vec4 refrColRed = RayMarchRefraction(pOut, rd, nOut, eta / CHROMATIC_ABERRATION, vec4(1,0,0,1));
        vec4 refrColGreen = RayMarchRefraction(pOut, rd, nOut, eta, vec4(0,1,0,1));
        vec4 refrColBlue = RayMarchRefraction(pOut, rd, nOut, eta * CHROMATIC_ABERRATION, vec4(0,0,1,1));

        //vec4 refrCol = max(max(refrColRed, refrColGreen), refrColBlue);
        vec4 refrCol = refrColRed + refrColGreen + refrColBlue;
        refrCol *= vec4(COLOR, 0.333);

        //col = refrCol;  // No reflection - pure refraction
        col = mix(refrCol, reflCol, 0.333);  // Refraction and reflection blended together
    }
    else
    {
        col = texture(iChannel0, rd);
    }

    fragColor = col;
}

#define Z iTime
#define T (Z<7.8?1.:.9*Z+exp(min(1.-Z+8.,1.)))*max(1.-(Z-2.),1.)*min(2.,max(1.,Z))+(1.-Z)
#define dmin(a,b)((a.x<b.x)?a:b)
#define Rt(p,a)(p=cos(a)*p+sin(a)*vec2(p.y,-p.x))

#define S smoothstep
#define R iResolution.xy

void scene0(out vec4, in vec2);
// https://www.shadertoy.com/view/MscSDB by Shane
// The path is a 2D sinusoid that varies over time, depending upon the frequencies, and amplitudes.
void path(inout vec2 p, inout float z){float s = sin(z/24.) * cos(z/16.); p += vec2(s*9.,1e-5);}

void L(in vec3 lp,in float lpd, inout float sp)
{
    vec2 sn = vec2(1);

    Rt(sn, 6.*T);
    lp = vec3(lp.xy+.5*sn,  lp.z-lpd);
    sp = length(lp)-.2;
}

vec2 f(in vec3 p) 
{
  float dl0 = 1e8, dl1 = 1e8;
  float zp = cos(T)*2.*p.z;
  path(p.xy, zp );
  vec3 lp = p;
  L(lp,7., dl0);
  L(vec3(-lp.xy, lp.z), 8., dl1);
  p.z+=8.*T;
  #define repM(_x,_y)(_y*.5-mod(_x,_y))
  vec3 gls =p,krs = p,kln = p;

  // -------- klingen
  kln.x = atan(p.y, p.x) * 2.86478;
  kln.xy = repM(vec2(kln.x, p.z), vec2(3));
  kln.z = length(vec2(p.x, p.y)) - (Z < 8.2 ? (1.4 + (sin(Z) * .5 + .5)) : 2.7);
  kln = abs(kln) - vec3(1., .125, .4);
  float fkln = length(max(vec3(.1001, -.1001, -.1), kln)) + min(max(kln.x, max(kln.y, kln.z)), 0.) - .125;

  // -------- kreise
  krs.xz = repM(vec2(0, p.z + 1.5), vec2(3));
  krs.y = length(p.xy) - 2. + (cos(T) * .2 + .2);
  krs = abs(krs) - vec3(.25, .25, .25);
  float fkrs = length(vec2(krs.y, krs.z)) - .125;

  // -------- gleise
  gls.x = T + atan(p.y, p.x) * 3.81971;
  gls.xz = repM(gls.xz, vec2(3., 1.125));
  gls.y = length(p.xy) - 2.;
  gls = abs(gls) - vec3(1, .125, .45);
  float fgls = length(max(vec3(-.124, .095, .05), gls)) + min(max(gls.x, max(gls.y, gls.z)), 0.) - .125;

  if(Z>15.&&Z<19.)
  {
  float tb = float(mod(Z, 4.)>1.);
  fgls = max(fgls,tb);
  fkrs = max(fkrs,tb);
  }
  if(Z>20.)fkln = 1000.;
  return dmin(vec2(min(dl1,dl0),7.),dmin(vec2(min(fkrs,fgls), 3.),vec2(fkln,5.)));
}

// https://www.shadertoy.com/view/4tdfDH
float ps(in vec3 p, in vec3 light_p)
{
    vec3 l = normalize(light_p - p);
    float t = 0.15;
    float t_max = distance(light_p, p);
    float res = 1.0;
    for (int i = 0; i < 64; ++i)
    {
        if (t > t_max)
            break;
        float d = f(p + t * l).x;
        if (d < 0.01)
            return 0.0;
        t += d;
        res = min(res, 8.0 * d / t);
    }
    return res;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 col = vec4(0.), bg = col;
    scene0(bg, fragCoord);

    vec2 RR = iResolution.xy;
    vec2 st = (2. * fragCoord.xy - RR.xy) / min(RR.x, RR.y);

    vec3 ro = vec3(0, 0, 0)+1e-4, rd = vec3(0);
    rd = normalize(vec3(st, 2.));


	// -------- raymarch
	const vec2 e = vec2(.0001,0);
	const float MAX_DIST =  20.;
	float h = 0.;
	vec3 p = ro;
    vec2 d = vec2(-1);
	for (uint i = 0u; i < 64u ; ++i)
	{
		d = f((ro + rd * h));
        if(abs(d.x) <( e.x*h) && h < MAX_DIST)break;
        h += d.x;

	} 

	if(h<MAX_DIST)
	{
    p = (ro + rd * h) ;
	vec3 N = normalize(vec3(f(p+e.xyy).x-f(p-e.xyy).x,f(p+e.yxy).x-f(p-e.yxy).x,f(p+e.yyx).x-f(p-e.yyx).x));

    vec3 lp0 = vec3(p.xy*.5+.5, 6.);
    vec3 lp1 = vec3(p.xy*.5+.5, 12.);

    float dmy = 1.;
    L(lp0, dmy, dmy);
    L(vec3(-lp1.xy, lp1.z), dmy,dmy);

    float lr0 =  8. / pow(max(0., dot(lp0 - p, lp0 - p)), 2.);
    float lr1 = 15. / pow(max(0., dot(lp1 - p, lp1 - p)), 2.);

    float dl = ps(p, lp0) * max(0.0, dot(N, normalize(lp0 - p)))  *  lr0 
               + ps(p, lp1) * max(0.0, dot(N, normalize(lp1 - p)) ) * lr1 ;

    float spc = pow(max(dot(N, -rd), 0.), 2.)+1e-5;


    col.xyz = vec3(0.745,0.729,0.953);

    col.xyz = mix(col.xyz*spc*h*.01, col.xyz*dl*spc, 1.0);

    if(d.y==7.)col.xyz = vec3(1);
	}

	fragColor =  vec4(pow(col.xyz, vec3(.454545)), 1.);

    (Z>8.&&Z<11.05)?
    fragColor = mix(col,bg, step(fract(2.65*T), .5)):
    (Z>24.)?
    fragColor = mix(col,bg, .1):fragColor;
}
float pvalnoise(in vec2 uv) 
{
        const float k = 212.;
        vec4 l = vec4(floor(uv), fract(uv));
        float u = l.x + l.y * k;
        vec4 v = vec4(u, u + 1.,u + k, u + k + 1.);
        v = fract(fract(1.23456789 * v) * v / .987654321);
        l.zw = l.zw * l.zw * (3. - 2. * l.zw);
        l.x = mix(v.x, v.y, l.z);
        l.y = mix(v.z, v.w, l.z);
        return mix(l.x, l.y, l.w);
}
float glw = 0.;
float f0(in vec3 p) 
{    
     Rt(p.xy, T);Rt(p.yz, T);
     float h = 0.1, w = .98, m = 12.8;
     vec3 v = vec3(.5+atan(p.z, p.x)/6.28, p.y, acos(p.y/length(p))/3.14159);
     for(int i = 1; i < 7; i++)
     h += S(-1.6, 2.7, ((w *=.5)*pvalnoise((v.xy*(m*=2.)))));
     float d = length(p)-1.5+.45*h;
     glw = 1. / ( 1.0 + pow(abs(d*.2),.5));
     return d;
}
vec3 N1(in vec3 p){
float d=f0(p);
vec2 e=vec2(.001,0);vec3 n=d-vec3(f0(p-e.xyy),f0(p-e.yxy),f0(p-e.yyx));return normalize(n);
}
void scene0( out vec4 O, in vec2 U )
{
   vec2 st = (U-.5*R)/max(R.y, R.x);
   vec4 c = vec4(0);
   #define repM(_x,_y)(_y*.5-mod(_x,_y))
   // tile screen
   st = repM(abs(st)-vec2(0., 0.), vec2(1., 1.));


    vec3 ro = vec3(-1.25, -1.75, 10.), //
         rd = normalize(vec3(st, -2.)),
         lp = vec3(1., 4., 5);
    float d = 0.;
    for(int i=0; i<18; i++) 
    {  
     float h = (f0((ro + rd * d)));
     if(abs(h)<.001) h=.42;
     c.w+=(.5+h)*.05;
     d += h;
    }
    vec3 p = ro + rd * d, 
         n = N1(p),
         ld = normalize(lp-p);
    c.xyz = vec3(0.502,0.251,0.251) * max(0., dot(n, ld))+pow(max(dot(n, -rd), 0.), 3.)+1e-5;
    c.xyz = mix(c.xyz, vec3(0.318,0.635,0.635)*glw, clamp(.5*c.w, 0., 1.));
    c.xyz = pow(c.xyz, vec3(.4545));
    O = c;
}

