#define _f(_x)(fract(_x))
#define S smoothstep
#define pi (4.*atan(1.))
#define pih (2.*atan(1.))


float flt(in float rt, in float y)
{
    float yy = 0.;
    yy = (sin(.5 * rt) * .5 + .5) + 1.29 - cos(2.71 * y) / tan(1.33 + sin(y));
    yy = mix(abs(y), cos(-2.71 * y) / (-1.39 + sin(y)), .5);
    return yy;
}

float drm_cl(float t, float ml, float mx)
{
    float ft = _f( mx * t );
    return sin(ml * 4084.015 * ft )  * exp( -100.* ft ) ;
}

float synth(in float rt, in float t, int ch)
{

    float yes = 0., yes1 = 0.;
    float yba = 0.;
    float yma = 0.;

    // float emx = 3.;
    float emx = 6.;
    if (rt > 8.)
        emx = 12.;
    if (rt > 20.)
        emx = 3.;

    float ft1 = _f(2.0 * t), ft2 = _f(emx * t), ft3 = _f(0.5 * t);
    float x = sin(ft1 * 0.04 + .5);
    float ph = x * 17152.863;
    vec4 vp = x + vec4(.81, .95, .63, 1.9);
    float p0 = ph * vp[int(emx * t) % 4];
    float p1 = ph * vp[int(.5 * t) % 4];

    float xb = ft3 * 0.02 + .5;
    vec4 vb = xb * vec4(1.89, 1., 2.01, 1.87);
    float p2 = 1.11 * 17152.863 * vb[int(.5 * t) % 4];

    yma = sin(p1); //soft with accents


    yes = atan(sin(p0), cos(p0)); //

    if (rt > 12. && rt < 24.) //====================INTRO electro synth
        yes = mix(atan(sin(p0), sin(.5 * p0)), atan(sin(p0), cos(p0)), (sin(2. * rt) * .5 + .5));

    yba = atan(sin(p2), cos(p2));//bass

    yma = flt(rt, yma);

    if (rt > 24.)//apply filter for smoother sound 
        yes = flt(rt, yes) * S(0., 5., (rt - 24.));

    yba = flt(rt, yba);


    yma *= min(1., 41.1 * ft1) * max(0., 1. - ft1);
    /*yma = clamp(yes, -1., 1.);

    return yma;
    */
    yes *= min(1., 44.1 * ft2) * max(0., 1. - ft2);
    yes = clamp(yes, -1., 1.);

    yba *= min(1., 44.1 * ft3) * max(0., 1. - ft3);


    //========================= SUPPORT INSTRUMENS

    float n = t * 8238.1;
    n = _f(n * _f(t + n)); //noise

    // claves
    float bft = mod(rt, 8.) > 2. ? 2. : 6.;
    bool dclkbol = mod(rt, 10.) > 8.;
    float one = 1.;
    float clk = dclkbol ? sin(8545.42955 * t + .7 * dot(one, n)) * exp(-5. * _f(bft * t)) : 0.;

    // weird sound
    float en = ft1; //_f(2. * t);
    bool ebol = mod(rt, 4.) > 1.;
    float efx = .5 * sin(t * vec3(13, 5, 17)[int(t) % 3]) + cos(13. * exp(1. - pow(1. - en, 100.)));
    float e = ebol ? 1e-1 :efx;
    float fxx = drm_cl(e + .5 * t, .125, 6.);

    if(mod(rt,102.)>92.)//====================OUTRO ?
    return 0.08*drm_cl(efx + .5 * t, .5, 1.)*S(1., 0., (mod(rt, 102.) - 95.)/4.);

    // bongos
    float ftbng = _f(6. * t);
    float g = 0.;
    g = mix(1.11, 1.48, smoothstep(0., 1., float(int(rt) % 3)));
    float bng = sin(1191.2549 * g * t) * exp(-5.5 * ftbng);
    bng *= float[](1., 0., 1., 0., 0., 1., 0., 1., 0., 1.)[int(3. * t) % 10];
    bng *= min(1., 188.2 * ftbng) * max(0., 1. - ftbng);

    //=================INTRO
    if(rt > 20.){
        #define I_ S(0.,
        #define _I rt-20.);

        yba *= I_ 2., _I 
        bng *= I_ 6., _I 
        clk *= I_ 1., _I 
        yma *= I_ 5., _I 
        fxx *= I_ 12., .9 * _I
    }
    else {
        fxx = 0.;clk = 0.;bng = 0.;yma = 0.;yba = 0.;
    }

    yes *= (rt < 24.) ? (1./2.) : 1.;


    if(mod(rt, 92.) < 50.)
    //if (rt < 50.)
    {
        //return ((yes + .5 * yma + .1 * clk) + ((.4 * yba + .5 * bng) + .4 * fxx)) *
        //       S(1., 0., (mod(rt, 92.) - 47.)/4.);//fade out

        return 
        (.3 * fxx + .3 * yes + .1 * clk  + .4 * yba + .6 * yma + .6 * bng) ;
             //  S(1., 0., (mod(rt, 92.) - 47.)/4.));//fade out
    }
    else{//====================BRIDGE    

        yba = atan(sin(4. * p2), cos(2.1 * p2));
        yba = clamp(yba, -1., 1.);
        yba *= min(1., 44.1 * ft3) * max(0., 1. - ft3);
        yba *= max(0., .35 * n + 1. - dot(_f(8. * t), _f(8. * t)));
        yba = mod(t, 6.) > 2. ? .25 * (yba) : 0.;

        float hht = clamp((1. - n * exp(-4. * _f(8. * t))), -1., 1.) * exp(-5. * _f(8. * t));
        float xe = pow(max(_f(2. * t), .004), .13);
        float kft = _f(3. * t);
        float kck = sin(250.5 * xe) * min(1., 44.1 * kft) * max(0., 1. - kft);
        //e = (.5 * sin(t * vec3(13, 5, 17)[int(t) % 3]) + cos(13. * exp(1. - pow(1. - en, 100.))));
        fxx = drm_cl(efx + .5 * t, (cos(rt) * .4 + .4) + .1, 2.);
        fxx *= (sin(rt) * .5 + .5);

        yma = .5 * sin(.1 + p1) + abs(.5 * sin(p1));
        yma *= S(0., 1., (mod(rt, 92.) - 58.) / 4.);
        return .7*(.7 * kck + .3 * hht + yba + .3 * yma + .2 * fxx);
    }

}

vec2 mainSound(in int samp, float t)
{

    float xx = 6.;
    float islVal2 = 2.8031;
    float islVal3 = 3.5301;

    if(t > 3.)
       xx = max(6. - (t - 3.), min(1.22, 6.));

    if(t > 7.7)
       islVal3 = min(4.7, islVal3 + (t - 7.3));

    if(t > 15.2)
       islVal2 = min(8.9841, islVal2 + (t - 9.));

    if(t > 24.)
    {
       islVal2 = 5.166;
       islVal3 = 3.1292893;
    }
    if(t > 32.)
    {
       islVal2 = min(5.707106, islVal2 + (t - 32.));
    }
    if(t > 40. && t < 46.)
    {
       islVal2 = 4.66;
       islVal3 = 4.78;
    }
    if(mod(t, 92.) > 50.)
    {
        islVal2 = 2.8031;
        islVal3 = 3.5301;

        if(mod(t, 6.) > 4.)
        {
            islVal2 = 6.8;
            islVal3 = 3.2;
        }
    }

    vec2 y = vec2(1e-6);
    vec4 v = vec4(0., 0., 0., 0.);

    float mt = 0.;
    (t < 16.) ? (mt = mod(t, 16.)):(mt = t);

    float dlt = mt;

    const vec4 dt = vec4(577, 601, 641, 661);

    /*
    wrote this comment while back, i have no idea what i means... hahaha

    the improvement here: the same gain coefficient, it makes it easier to control
    in this case a hole number on the smoothstep makes the reverberation stable  e.g 7.2 + 3.7
    */

    const float g = 1. / 1200.;

    const vec4 A = vec4(0, -1, 1, 0) * g;

    for(uint j = 0u; j < 4u; ++j)
        for(uint i = 0u; i < 12u; ++i)
        {
            float fdn = 0.;
            fdn += smoothstep(-islVal3, islVal2, dot(A, dt) * g);
            dlt += fdn;

            v.xw = vec2(.7) * synth(mt, dlt, 0) * exp(-xx * fdn);
            v.yz = vec2(.2) * synth((mt + 1e-5 * (11. * sin(mt) + 15. * cos(mt))), mt, 0);
            (i % 2u == 0u) ? (y.x += v[i % 4u]) : (y.y += v[i % 4u]);
        }

    (y*=(1./6.));  
    return y;
}

