void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scale factor to enlarge image by
    float sf = dim.y / (iResolution.y - dim.y);
    sf *= 0.75;

    // Scale and centre image to screen
    vec2 coord = round(sf * fragCoord) ;
    coord -= round(sf * 0.5 * iResolution.xy - 0.5 * dim);

    vec3 col = texelFetch(iChannel0, ivec2(coord), 0).rgb;

    // Quick and dirty outline
    vec3 t = texelFetch(iChannel0, ivec2(coord + vec2(0,1)), 0).rgb;
    vec3 b = texelFetch(iChannel0, ivec2(coord - vec2(0,1)), 0).rgb;
    vec3 r = texelFetch(iChannel0, ivec2(coord + vec2(1,0)), 0).rgb;
    vec3 l = texelFetch(iChannel0, ivec2(coord - vec2(1,0)), 0).rgb;

    vec3 sm = t + b + l + r;
    if (col == vec3(0) && length(sm) > 0.)
        col = vec3(0.01);   

    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 ms = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;

    float d = length(uv - ms);
    float k = 1. / iResolution.y;
    float s =  smoothstep(-k, k, -abs(d-0.025) + 0.005);
    s = max(s, smoothstep(-k, k, -abs(0.25-abs(uv.y)) + 0.005));

    // Background + color y-bounds + mouse position
    if (col == vec3(0.)) {
        vec3 col2 = clamp(1. - 0.3 * uv.x * uv.x, 0.75, 0.974) * vec3(1);
        col = mix(vec3(0.974), col2, s);
    }
    else
        col = sqrt(col) + 0.08;

    fragColor = vec4(col, 1.);
}

