#define pi 3.14159
#define thc(a,b) tanh(a*cos(b))/tanh(a)

#define lineCol(x) ( .5 + .5 * thc(4., x+2. * pi / 3.*vec3(-1,0,1)) )

#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float line(in vec2 f, in vec2 p, in vec2 q) { 
    // Translate space. We consider 0,0 to p-q
    f -= p;

    vec2 pq = p - q;   
    float a = atan(pq.x, pq.y) + pi / 2.;

    // Rotate f
    float c = cos(a), s = sin(a);    
    float x = c * f.x - s * f.y;
    float y = s * f.x + c * f.y;

    // Split space into 1 (above rotated y), 0 (below)
    float e = step(y, 0.);

    // Do the same for 4 neighbouring cells (in non-rotated space)
    float t = step(y,  c);
    float b = step(y, -c);
    float r = step(y,  s);
    float l = step(y, -s);  

    // If some neighbours are different, we draw a line
    // Otherwise, we don't (0 0 0 0, 1 1 1 1 -> 0 )  
    float m = max(max(t,b),max(l,r)) - min(min(t,b),min(l,r));
    // m = step(1., mod(t + b + l + r, 4.));

    // Center x at d (in rotated space). Only include cells within d of it.
    // (only include cells between p and q)
    float d = 0.5 * length(pq);  
    return e * m * step(abs(x - d), d);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 ms = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;

    // Change colors above + below y = 0 (unused)
    float drk = step(0.5 * dim.y, fragCoord.y);
    drk = 0.;

    // Center fragCoord + mirror y axis
    fragCoord -= 0.5 * dim;
    fragCoord.y = abs(fragCoord.y);
    vec3 col = vec3(0);    

    // Use this to flick through patterns. Resets "seed" every 60 minutes (I think)
    // (using mod so hash function doesn't mess up)
    float fl = mod(floor(floor(iDate[3]/3600.) + iTime), 600.);

    vec2 f = fragCoord;
    vec2 p = vec2(0);

    // Maybe change (static center pixel looks bad)
    float a0 = 0.;

    float n = 120.;
    for (float i = 0.; i < n; i++) {
        // Random values for line positions
        float h = h21(vec2(i + fl + 11., 0.131 * i - 103.));

        a0 += 2. * pi * cos(0.1 * ms.x + h * pi * 2.);

        // New point (2. * (...) and q == floor(q) is interesting too)
        vec2 q = p + 1. * vec2(cos(a0), sin(a0));
        q = round(q);

        // Makes them chunky + weird
        // f.y = abs(f.y - 0.2);

        // Color (todo: add noise for texture, maybe)
        vec3 e = vec3(0.5);
        vec3 col2 = pal(4. * i/n + 0.25 * fl - 0.25 * drk, e, e, e, 
                        mod(i - 2. * n * ms.y + 33. * fl, n) / n * vec3(0,1,2)/3.);

        //col2 = lineCol(4. * i/n + 0.1* fl);
        col2 *= line(f, p, q);

        // Remove earliest lines in sequence
        // col2 *= step((0.5 + 0.5 * cos(10. * ms.y)) * n, i);

        // Alternate color palette
        //col += 0.333 * col2;

        if (col == vec3(0))
            col = col2;

        p = clamp(q, vec2(-0.5 * dim.y, 0), 0.5 * dim);
    }

    fragColor = vec4(col, 1.);
}

