void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouseRel = iMouse.xy / iResolution.xy;//vec2(1280.,720.);
    vec2 relCenter = vec2(0.5,0.5);


    // Rotation Direction
    vec3 baseValue = vec3(0,2,4);
    vec3 initialRotationDirection = baseValue;

    // Brightness
    float distanceFromCenter = length(mouseRel.xy-relCenter);
    float relCenterRadius = 0.1;
    float distanceSensitivity = 20.;
    float inTheZone = max(relCenterRadius,distanceFromCenter) - relCenterRadius;
    float brightness = 1. - distanceSensitivity*inTheZone;

    // Circle
    float distanceFromMouse = 5.*length(uv - mouseRel);
    float fuzzyFactor1 = 5.;
    float fuzzyFactor2 = 1.6;
    float centerBright = 5.;
    vec3 circleArea = vec3(centerBright*brightness + fuzzyFactor1*pow(distanceFromMouse,fuzzyFactor2));

    // Time varying pixel color
    vec3 timeVarying = cos(iTime+uv.xyx+initialRotationDirection);



    vec3 col = 0.5 + distanceFromCenter*circleArea *0.03 + 0.5*timeVarying;


    // Output to screen
    fragColor = vec4(col,1.0);
}

float nrand(float x, float y) 
{
    return fract(sin(dot(
        vec2(x, y), vec2(12.9898, 78.233))) * 
        43758.5453); 
}
vec4 Blend(vec4 top, vec4 bottom) 
{
	vec4 result;
	result.a = top.a + bottom.a * (1.0 - top.a);
	result.rgb = (top.rgb * top.aaa + bottom.rgb * bottom.aaa * 
        (vec3(1.0, 1.0, 1.0) - top.aaa)) / result.aaa;
	return result;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float jitter = nrand(uv.y, iTime / 20.) * 2. - 1.;
    uv.x += jitter * step(0.0, abs(jitter)) * 0.00175;

    vec2 texel = 1. / iResolution.xy;
	vec3 duv = texel.xyx * vec3(0.5, 0.5, -0.5);

	vec3 blur = texture(iChannel0, uv.xy).rgb;

    float sub = -0.1;
    float hard = 0.3;

    float modulo = floor(mod(uv.x / texel.x * 0.25, 3.0));
    vec3 tmp = blur.rgb;
    float is0 = step(modulo, 0.) * step(0., modulo);
    float is1 = step(1., modulo) * step(modulo, 1.);
    tmp -= vec3(0., sub * hard, 
        sub * hard * 2.0) * is0;
    tmp -= vec3(sub * hard, 0., 
        sub * hard) * step(1., modulo) * step(modulo, 1.);
    tmp -= vec3(sub * hard * 2.0, 
        sub * hard, 0.) * (1. - is0) * (1. - is1);
    vec3 col = Blend(vec4(tmp, 0.75), vec4(blur, 1.5)).rgb;

    float scanline = 
        sin((uv.y - sin(iTime / 200.)) * iResolution.y) * 0.015;
    col.rgb -= scanline;

    vec2 vigUV = fragCoord.xy / iResolution.xy * 2. - 1.;
    float vignette = smoothstep(-1., .75, 1. - dot(vigUV, vigUV));
    col *= vignette;
    fragColor = vec4(col, 1.0);
}

