mat3 YRotationMatrix(float degrees)
{
	float angleY = radians(degrees);
	float c = cos(angleY);
	float s = sin(angleY);
	mat3 rotateYMatrix = mat3(
        c, 0, s,
		0, 1, 0,
		-s, 0, c);
	return rotateYMatrix;
}
mat3 ZRotationMatrix(float degrees)
{
	float angleZ = radians(degrees);
	float c = cos(angleZ);
	float s = sin(angleZ);
	mat3 rotateZMatrix = mat3(
        c, -s, 0,
		s, c, 0,
		0, 0, 1
		);
	return rotateZMatrix;
}
mat3 XRotationMatrix(float degrees)
{
	float angleX = radians(degrees);
	float c = cos(angleX);
	float s = sin(angleX);
	mat3 rotateXMatrix = mat3(
        1, 0, 0,
		0, c, -s, 
		0, s, c);
	return rotateXMatrix;
}
void Rotate(inout vec3 o, vec3 a)
{
    vec3 localScaledTranslatedRotX = o * XRotationMatrix(a.x);
    vec3 localScaledTranslatedRotXY = localScaledTranslatedRotX * YRotationMatrix(a.y);
    o = localScaledTranslatedRotXY * ZRotationMatrix(a.z);
}

