void mainImage( out vec4 O, vec2 U )
{
    O = vec4( U.y < T(vec2(U.x,0)).x );                    // draw curve
    vec4 M = T(2);                                         // load all stats
    if ( abs(M.z-U.x) < M.w ) O = mix(O,vec4(0,1,0,1),.4); // trace std-dev(x)
    if ( abs(M.z-U.x) < .5  ) O =       vec4(0,1,0,1);     // trace average(x)
  if (U.x>M.z) {
    if ( abs(M.x-U.y) < M.y ) O = mix(O,vec4(1,0,0,1),.5); // trace std-dev(y)
    if ( abs(M.x-U.y) < .5  ) O =       vec4(1,0,0,1);     // trace average(y)
 }else{
    M = T(3);                                              // stats on defined part only
    if ( abs(M.x-U.y) < M.y ) O = mix(O,vec4(1,0,0,1),.5); // trace std-dev(y)
    if ( abs(M.x-U.y) < .5  ) O =       vec4(1,0,0,1);     // trace average(y)
    }
}

