#define keyClick(a)   ( texelFetch(iChannel3,ivec2(a,1),0).x > 0.)
#define keyDown(a)    ( texelFetch(iChannel3,ivec2(a,2),0).x > 0.)

void mainImage( out vec4 O, vec2 U )
{
    if (U==vec2(1.5)) O.xy = iMouse.xy; // memorize last mouse position

    float t, n = 0., v = 0.;
    if (U==vec2(2.5)||U==vec2(3.5)) {   // computes moments 1,2 in x and y
        O = vec4(0);                    // (not normalized yet)
        for (float x = 0.; x < R.x; x++ )
            t = T(vec2(x,0)).x,
            O += vec4( t, t*t, t*x, t*x*x),
            n++,                        // counts all values
            v += step(1e-6,t);          // counts non-zero values
    }
    if (U==vec2(2.5)) {                 // computes stats in x and y    
        O /= n; O.zw /= O.x;            // order-1 and order-2 moments in x and y
     // O.x                             // average y
        O.y = sqrt(O.y - O.x*O.x);      // std-dev y
     // O.z                             // average x
        O.w = sqrt(O.w - O.z*O.z);      // std-dev x
    }
    if (U==vec2(3.5)) {                 // computes stats for defined values
        O /= v;                         // order-1 and order-2 moments in y
     // O.x                             // average y
        O.y = sqrt(O.y - O.x*O.x);      // std-dev y
    }
    if (U.y>1.) return;

    O = T(U);                           // draw curve between prev-mouse and mouse
    vec2 M = T(1).xy;
    float a = min(M.x,iMouse.x), b = max(M.x,iMouse.x);
    if (iMouse.z>0. && iMouse.w<0. && U.x >= a && U.x <= b )
        O.x = keyDown(32) ? 0.                   // SPACE : erase area
              : mix( M.y, iMouse.y,(U.x-M.x)/(iMouse.x-M.x) );

    if ( keyClick(32) &&iMouse.z<0.)  O=vec4(0); // SPACE : erase all

    if (iFrame==0 ||                    // default value and presets 1,2,3
        keyClick(49)) U = 2.*U/R.x-1., O.x = R.y*exp(-.5*U.x*U.x*16.);
    if (keyClick(50)) U = abs(2.*U/R.x-1.)-.5, O.x = R.y*exp(-.5*U.x*U.x*64.); 
    if (keyClick(51)) U = U/R.x, O.x = R.y*U.x*exp(-10.*U.x)/.037;      // / f(1/5)
}

