float linf(vec2 p) {
    return max(abs(p.x),abs(p.y));
}

vec3 parabola_SDF(vec2 p, float h) {
    vec2 p2 = p - min(p.x+p.y,0.)*.5;
    float t = (sqrt(1.-4.*h*(-abs(p2.x)-p2.y))-1.)/h/2.;
    t*=p.x<0.?-1.:1.;
    vec2 closest = vec2(t,t*t*h);
    float dist = linf(p-closest) * sign(p.x*p.x*h-p.y);
    return vec3(dist,closest);
}

vec3 shadeDistance(float d) {
    d *= .5;
    float dist = d*120.;
    float banding = max(sin(dist), 0.0);
    float strength = sqrt(1.-exp(-abs(d)*2.));
    float pattern = mix(strength, banding, (0.6-abs(strength-0.5))*0.3);

    vec3 color = vec3(pattern);

    color *= d > 0.0 ? vec3(0.769,0.918,0.624) : vec3(0.671,0.506,0.875);

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
	float scale = 5.;
    uv*=scale; mouse*=scale;
    uv.y+=1.; mouse.y+=1.;
    float pixel_size = scale/iResolution.y;

    float t = sin(iTime)*.25+.75;
    float h = mix(.1,20.,pow(t,8.));

    vec3 mousedist = parabola_SDF(mouse, h);
    vec3 col = shadeDistance(parabola_SDF(uv, h).x);
    if (iMouse.z > 0.) {
        float dst = max(abs(mouse.x-uv.x),abs(mouse.y-uv.y));
        col *= smoothstep(-pixel_size,pixel_size, dst - abs(mousedist.x)) *.5 +.5;
        col = mix(vec3(0.396,0.894,0.902), col, smoothstep(-pixel_size,pixel_size, distance(mousedist.yz, uv) - .05));
    }
    //float sn = abs(uv.y+cos(uv.x*h))-.01;
    //float snl = 0.*smoothstep(fwidth(sn),0.,sn);

    fragColor = vec4(col,1.0);// + snl;
}

// 0.5 is perfect for a thin line,
// larger values are too sharp,
// smaller values (for outlines & such) like 0.01 reduce pixel quality for low-bit textures, e.g. R8
#define SHARPNESS 0.5

float smax(float a, float b, float k){
   if(k <= 0.0) return max(a,b);
   float h = max(k-abs(a-b), 0.0)/k;
   float m = h*h*h*0.5;
   float s = m*k*(1.0/3.0); 
   return max(a,b)+s;
}
float smin(float a, float b, float k){
    return -smax(-a,-b,k);
}

mat2 rot(float a){
    float c = cos(a), s = sin(a);
    return mat2(c,-s,s,c);
}

float sdf(vec2 p){
    p.x -= (floor(p.x*6.0)+0.5)/6.0; // repeat shape on x-axis
    p.y -= (clamp(floor(p.y*4.0),-1.0,0.0)+0.5)/4.0; // repeat shape on y-axis
    float scale = 10.0;
    // if sb wants to, he can create a nice letter here :)
    p *= scale;
    // sloped edge with small curvature?? doesn't show obvious problems...
    // I get issues at 22.5° with a 1px thin curve (res=60, sdf=max(length(p)-0.5, 0.3-length(p))),
    // but that's because of resolution; set it to 120 and everything is fine
    // float sdf = length(p-vec2(4.0))-5.5;
    // standard shape: a circle, which is cut off from the top
    // float sdf = max(length(p)-0.5, p.y+sin(iTime)*.5-.2);
    // new shape: the letter f
    vec2 p0 = p;
    p *= rot(-0.1);
    p.x += 0.1;
    if(p.y < 0.0) p = -p;
    vec2 q1 = p-vec2(0.64,0.3);
    vec2 q2 = p-vec2(0.64,0.3);
    float sdf = max(length(q1)-0.8, 0.6-length(q2));
    sdf = smin(sdf,smax(abs(p.y)-0.1,abs(p.x)-0.3,0.1),0.04);
    sdf = smax(sdf,abs(dot(p,vec2(1.0,0.3)))-0.7,0.2);
    // add original shape ^^
    p0 *= 2.0;
    p0 += vec2(-1.0,1.5);
    sdf = min(sdf,0.5*max(length(p0)-0.5, p0.y+sin(iTime)*.5-.2));
    sdf /= scale;
    return sdf;
}

float nearestTexture(vec2 p, float res){
    // make pixelated
    p = round(p*res)/res;
    float raw = clamp(sdf(p)*res*SHARPNESS+0.5, 0.0, 1.0);
    return round(raw*255.0)/255.0;// discretize to 8 bit 
}

float linearTexture(vec2 p, float res){
    p *= res;
    vec2 index = floor(p)/res;
    vec2 delta = vec2(0.0,1.0/res);
    vec2 frac = fract(p);
    float c00 = nearestTexture(index + delta.xx, res);
    float c01 = nearestTexture(index + delta.xy, res);
    float c10 = nearestTexture(index + delta.yx, res);
    float c11 = nearestTexture(index + delta.yy, res);
    return mix(
        mix(c00,c01,frac.y),
        mix(c10,c11,frac.y),
        frac.x
    );
}

// bicubic interpolation from Inigo Quilez, https://www.shadertoy.com/view/XsSXDy
const vec4 BS_A = vec4(   3.0,  -6.0,   0.0,  4.0 ) /  6.0;
const vec4 BS_B = vec4(  -1.0,   6.0, -12.0,  8.0 ) /  6.0;
const vec4 RE_A = vec4(  21.0, -36.0,   0.0, 16.0 ) / 18.0;
const vec4 RE_B = vec4(  -7.0,  36.0, -60.0, 32.0 ) / 18.0;
const vec4 CR_A = vec4(   3.0,  -5.0,   0.0,  2.0 ) /  2.0;
const vec4 CR_B = vec4(  -1.0,   5.0,  -8.0,  4.0 ) /  2.0;

vec4 ca=RE_A, cb=RE_B;
// vec4 ca=CR_A, cb=CR_B;
vec4 powers( float x ) { return vec4(x*x*x, x*x, x, 1.0); }
float spline(float x, float c0, float c1, float c2, float c3){
    return c0 * dot(cb, powers(x + 1.0)) + 
           c1 * dot(ca, powers(x      )) +
           c2 * dot(ca, powers(1.0 - x)) +
           c3 * dot(cb, powers(2.0 - x));
}

#define SAM(a,b) nearestTexture((i+vec2(a,b))/res,res)
float bicubicTexture(vec2 p, float res){
    vec2 f = fract(p*res);
    vec2 i = floor(p*res);
    return spline( f.y, spline( f.x, SAM(-1,-1), SAM( 0,-1), SAM( 1,-1), SAM( 2,-1)),
                        spline( f.x, SAM(-1, 0), SAM( 0, 0), SAM( 1, 0), SAM( 2, 0)),
                        spline( f.x, SAM(-1, 1), SAM( 0, 1), SAM( 1, 1), SAM( 2, 1)),
                        spline( f.x, SAM(-1, 2), SAM( 0, 2), SAM( 1, 2), SAM( 2, 2)));
}

// sdf colors from Inigo Quilez (e.g. https://www.shadertoy.com/view/4llXD7)
vec3 colorize(float sdf){
    float d = sdf*6.0;
    float sh = 10.0*length(vec2(dFdx(d),dFdy(d)));
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
    col *= 1.0 - exp(-50.0*abs(d));
    col *= 0.8 + 0.2*cos(150.0*d);
    return mix( col, vec3(1.0), 1.0-smoothstep(0.0,sh*0.3,abs(d)) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.x;

    // texture resolution over whole screen, based on y axis
    float res = 120.0;
    // how many slices are shown
    int numSlices = 6;
    // the second implementation here is animated
    // int slice = int(float(fragCoord.x)/float(iResolution.x) + (sin(iTime*3.0/float(numSlices))*.5+.5)*float(numSlices-1));
    int slice = (numSlices*int(fragCoord.x))/int(iResolution.x);
    // sample our texture with nearest or with linear filtering
    float nearestPixel = nearestTexture(uv,res);
    float linearPixel  = linearTexture( uv,res);
    float bicubicPixel = bicubicTexture(uv,res);
    // display the difference between the techniques and the raw, unfiltered texture
    vec3 col;
    float sh, f;
    vec3 white = vec3(1.0);
    bool halfY = fragCoord.y*2.0 > iResolution.y;
    switch(slice){
        case 0:
            // sdf itself
            f= sdf(uv);
            sh = length(vec2(dFdx(f),dFdy(f)));
            col = halfY ? vec3(smoothstep(-sh,sh,f)) : colorize(f);
            break;
        case 1:
            // raw texture
            col = halfY ? vec3(nearestPixel) : white;
            break;
        case 2:
            // linearly filtered texture = alpha approach
            col = halfY ? vec3(linearPixel) : white;
            break;
        case 3:
            // signed distance approach
            f = (linearPixel-0.5)*SHARPNESS;
            sh = length(vec2(dFdx(f),dFdy(f)));
            col = halfY ? vec3(smoothstep(-sh, +sh, f)) : colorize((linearPixel-0.5)/res/SHARPNESS);
            break;
        case 4:
            // bicubic filtered texture
            col = halfY ? vec3(bicubicPixel) : white;
            break;
        case 5:
            // bicubic filtered texture, then sdf
            f = (bicubicPixel-0.5)*SHARPNESS;
            sh = length(vec2(dFdx(f),dFdy(f)));
            col = halfY ? vec3(smoothstep(-sh,+sh,f)) : colorize((bicubicPixel-0.5)/res/SHARPNESS);
            break;
    }
    fragColor = vec4(vec3(col),1.0);
}

//Itterations to smooth the noise
float octaves = 8.;
//"Size" of the sampled noise texture | Doubles each itteration
float frequency = 0.125;
//"Height" of the sampled value | Halfes each itteration
float amplitude = 1.;
//Size of the uv space | 1 = noise texture is sampled once across the screen, 2 = twice, etc.
float size = 1.0;
//Final color of the pixel
vec4 final = vec4(0.);
//Radius of the circle in which the noise is shown

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy/iResolution.xy)*size;

    for(float i = 0.; i < octaves; i++)
    {
        //Sample region which increases each itteration
        vec4 col = texture(iChannel0, (uv*frequency));

        //Just to get it to be black and white
        col = vec4(col.x *= amplitude);
        //You want to see it, right?
        col.w = 1.0;

        //Added as it is normalized at the end
        final += col;

        //Double/Half the values for the next itteration
        frequency *= 2.;
        amplitude *= .5;

    }

    //Normalize to not just have a white image and multiply with mask to get a smooth falloff
    final = (normalize(final)*(octaves/2.));

    //Calculate a circle and mulriply the distance difference by the current value to get the distortion
    float dist = sqrt((fragCoord.x - iResolution.x/2.)*final.x*(fragCoord.x - iResolution.x/2.)*final.x+(fragCoord.y - iResolution.y/2.)*final.x*(fragCoord.y - iResolution.y/2.)*final.x);

    fragColor = vec4(0.3, 0.3, 1, 1);

    //Color if inside of the Landmass
    if(dist <= 102. + 3.*((sin(iTime)+1.)/2.))
       fragColor = vec4(1, 1, 1, 1);
    if(dist <= 100.)
    {
       if(dist >= 80.)
           fragColor = vec4(0.95, 0.8, 0, 1);
       else
           fragColor = vec4(0., 0.7, 0., 1.);
    }
}

